CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.alterar_situacao_auditor ( nr_seq_visita_auditor_p pls_visita_tecnica_auditor.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

nr_seq_visita_w			pls_visita_tecnica.nr_sequencia%type;
nm_usuario_auditor_w		pls_visita_tecnica_auditor.nm_usuario_auditor%type;
ie_auditor_responsavel_w	pls_visita_tecnica_auditor.ie_auditor_responsavel%type;
ie_situacao_ant_w		pls_visita_tecnica_auditor.ie_situacao%type;
ie_situacao_w			pls_visita_tecnica_auditor.ie_situacao%type;
ds_situacao_w			varchar(255);

BEGIN

begin
select	nr_seq_visita,
	nm_usuario_auditor,
	ie_auditor_responsavel,
	ie_situacao
into STRICT	nr_seq_visita_w,
	nm_usuario_auditor_w,
	ie_auditor_responsavel_w,
	ie_situacao_ant_w
from	pls_visita_tecnica_auditor
where	nr_sequencia	= nr_seq_visita_auditor_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

if (ie_auditor_responsavel_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207809, 'NM_AUDITOR='||obter_nome_usuario(nm_usuario_auditor_w));
end if;

if (ie_situacao_ant_w = 'I') then
	ie_situacao_w	:= 'A';
	ds_situacao_w	:= obter_desc_expressao(695840);
else
	ie_situacao_w	:= 'I';
	ds_situacao_w	:= obter_desc_expressao(831342);
end if;

update	pls_visita_tecnica_auditor
set	ie_situacao	= ie_situacao_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_visita_auditor_p;

CALL CALL pls_visita_tecnica_prest_pck.gravar_log_sistema(	nr_seq_visita_p	=> nr_seq_visita_w,
				ie_tipo_log_p	=> 3,
				ds_log_p	=> wheb_mensagem_pck.get_texto(1207797, 'NM_AUDITOR='||obter_nome_usuario(nm_usuario_auditor_w)||';DS_SITUACAO='||ds_situacao_w ),
				nm_usuario_p	=> nm_usuario_p,
				ie_commit_p	=> 'N');

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
