CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.aprovar_prazo_solicitado ( nr_seq_historico_p pls_visita_tecnica_hist.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

nr_seq_inconsistencia_w		pls_visita_tecnica_inco.nr_sequencia%type;
dt_prazo_solicitado_w		pls_visita_tecnica_hist.dt_prazo_solicitado%type;

BEGIN

begin
select	a.nr_seq_inconsistencia,
	a.dt_prazo_solicitado
into STRICT	nr_seq_inconsistencia_w,
	dt_prazo_solicitado_w
from	pls_visita_tecnica_hist	a,
	pls_visita_tecnica_inco	b
where	b.nr_sequencia		= a.nr_seq_inconsistencia
and	a.nr_sequencia		= nr_seq_historico_p
and	a.ie_status_prazo_inconsistencia = 1 --Pendente
and	b.ie_status		= 2; --Aguardando retorno;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

CALL pls_visita_tecnica_prest_pck.alterar_prazo_inconsistencia(	nr_seq_inconsistencia_p	=> nr_seq_inconsistencia_w,
				dt_novo_prazo_p		=> dt_prazo_solicitado_w,
				nm_usuario_p		=> nm_usuario_p,
				ie_commit_p		=> ie_commit_p);

update	pls_visita_tecnica_hist
set	ie_status_prazo_inconsistencia = 2,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_historico_p;

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
