CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.avaliar_inconsistencia ( nr_seq_visita_p pls_visita_tecnica.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE


dt_visita_w	pls_visita_tecnica.dt_visita%type;


BEGIN

begin
select	dt_visita
into STRICT	dt_visita_w
from	pls_visita_tecnica
where	nr_sequencia	= nr_seq_visita_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

update	pls_visita_tecnica_inco
set	ie_status	= 2,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_visita	= nr_seq_visita_p
and	ie_status	= 1;

update	pls_visita_tecnica
set	ie_status		= 8,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_visita_p;

CALL CALL pls_visita_tecnica_prest_pck.gravar_log_sistema(	nr_seq_visita_p	=> nr_seq_visita_p,
				ie_tipo_log_p	=> 9,
				ds_log_p	=> wheb_mensagem_pck.get_texto(1210151,'DT_AGENDAMENTO='||to_char(dt_visita_w,'dd/mm/yyyy hh24:mi')),
				nm_usuario_p	=> nm_usuario_p,
				ie_commit_p	=> 'N');

CALL CALL pls_visita_tecnica_prest_pck.gerar_alerta_evento(	nr_seq_visita_p		=> nr_seq_visita_p,
			ie_tipo_processo_p	=> 9,
			nm_usuario_p		=> nm_usuario_p,
			ie_commit_p		=> 'N');

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
