CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.excluir_visita_form ( nr_seq_visita_form_p pls_visita_tecnica_form.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

nr_seq_form_origem_w	pls_visita_tecnica_form.nr_seq_form_origem%type;

BEGIN

begin
select	nr_seq_form_origem
into STRICT	nr_seq_form_origem_w
from	pls_visita_tecnica_form
where	nr_sequencia	= nr_seq_visita_form_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

if (nr_seq_form_origem_w IS NOT NULL AND nr_seq_form_origem_w::text <> '') then
	delete	from	pls_visita_tecnica_form_pr
	where	nr_seq_visita_form	= nr_seq_visita_form_p;
	
	delete	from	pls_visita_tecnica_form
	where	nr_sequencia	= nr_seq_visita_form_p;
	
	update	pls_visita_tecnica_form
	set	ie_situacao	= 'A',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_form_origem_w;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1208764); --O formulario selecionado nao pode ser excluido
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
