CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.inativar_formulario ( nr_seq_formulario_p pls_formulario_visita.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_contador_w	integer;

BEGIN

begin
select	count(1)
into STRICT	nr_contador_w
from	pls_formulario_visita
where	nr_sequencia = nr_seq_formulario_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	ie_situacao = 'A';
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

if (nr_contador_w > 0) then
	update	pls_formulario_visita
	set	dt_inativacao	= clock_timestamp(),
		ie_situacao	= 'I',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_formulario_p;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
