CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.liberar_historico ( nr_seq_historico_p pls_visita_tecnica_hist.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_resultado_w		varchar(1);
nr_seq_visita_w		pls_visita_tecnica_hist.nr_seq_visita%type;
ie_status_w		pls_visita_tecnica.nr_sequencia%type;
dt_sugerida_visita_w	pls_visita_tecnica_hist.dt_sugerida_visita%type;
ie_agendamento_w	pls_tipo_hist_visita_tec.ie_agendamento%type;
qt_formulario_w		integer;

BEGIN

begin
select	coalesce(t.ie_agendamento,'N') ie_agendamento,
	a.dt_sugerida_visita,
	b.nr_sequencia nr_seq_visita,
	b.ie_status
into STRICT	ie_agendamento_w,
	dt_sugerida_visita_w,
	nr_seq_visita_w,
	ie_status_w
from	pls_visita_tecnica_hist a,
	pls_visita_tecnica b,
	pls_tipo_hist_visita_tec t
where	b.nr_sequencia	= a.nr_seq_visita
and	t.nr_sequencia	= a.nr_seq_tipo_historico
and	a.nr_sequencia	= nr_seq_historico_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

if (ie_agendamento_w = 'S') then
	if (ie_status_w not in (1,7)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1207531);
	end if;

	if (dt_sugerida_visita_w < trunc(clock_timestamp(),'dd')) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1207532);
	end if;

	begin
	select	'S'
	into STRICT	ie_resultado_w
	from	pls_visita_tecnica_hist
	where	nr_seq_visita	= nr_seq_visita_w
	and	nr_sequencia	<> nr_seq_historico_p
	and	ie_status_agendamento in (1,2)
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	exception
	when no_data_found then
		ie_resultado_w := 'N';
	end;

	if (ie_resultado_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1207533);
	end if;

	select	count(1)
	into STRICT	qt_formulario_w
	from	pls_visita_tecnica_form
	where	nr_seq_visita	= nr_seq_visita_w
	and	ie_responsavel	= 'O'
	and	ie_status	= 1;

	if (qt_formulario_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1211026);
	end if;

	update	pls_visita_tecnica
	set	ie_status	= 2,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_visita_w;
	
	CALL CALL pls_visita_tecnica_prest_pck.gerar_alerta_evento(	nr_seq_visita_p		=> nr_seq_visita_w,
				ie_tipo_processo_p	=> 1,
				nm_usuario_p		=> nm_usuario_p,
				ie_commit_p		=> 'N');
end if;

update	pls_visita_tecnica_hist
set	dt_liberacao		= clock_timestamp(),
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_historico_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
