CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.liberar_visita ( nr_seq_visita_p pls_visita_tecnica.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

nr_seq_formulario_w	pls_formulario_visita.nr_sequencia%type;
ie_status_w		pls_visita_tecnica.ie_status%type;

BEGIN

begin
select	b.nr_seq_formulario,
	a.ie_status
into STRICT	nr_seq_formulario_w,
	ie_status_w
from	pls_visita_tecnica	a,
	pls_tipo_visita_tecnica	b
where	b.nr_sequencia	= a.nr_seq_tipo_visita
and	a.nr_sequencia	= nr_seq_visita_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

if (ie_status_w = 0) then
	if (nr_seq_formulario_w IS NOT NULL AND nr_seq_formulario_w::text <> '') then
		gerar_formulario_visita(nr_seq_visita_p, nr_seq_formulario_w, 'P', nm_usuario_p,'N');
		gerar_formulario_visita(nr_seq_visita_p, nr_seq_formulario_w, 'O', nm_usuario_p,'N');
	end if;

	update	pls_visita_tecnica
	set	ie_status		= 1,
		nr_seq_formulario	= nr_seq_formulario_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_visita_p;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
