CREATE OR REPLACE FUNCTION pls_visita_tecnica_prest_pck.lista_auditores ( nr_seq_visita_p pls_visita_tecnica.nr_sequencia%type, ie_responsavel_p pls_visita_tecnica_auditor.ie_auditor_responsavel%type) RETURNS varchar AS $body$
DECLARE

dt_visita_w		pls_visita_tecnica_hist.dt_fim_visita%type;
ds_auditores_w		varchar(500)	:= null;

C01 CURSOR FOR
	SELECT	nm_auditor,
		row_number() OVER () AS nr_linha,
		count(1) over () qt_linha
	from (
		SELECT	obter_nome_usuario(a.nm_usuario_auditor) nm_auditor
		from	pls_visita_tecnica_auditor a
		where	a.ie_situacao		= 'A'
		and	a.nr_seq_visita		= nr_seq_visita_p
		and (ie_auditor_responsavel	= ie_responsavel_p or ie_responsavel_p = 'N')
		and	not exists (select	1
					from	pls_visita_tecnica_hist hh,
						pls_tipo_hist_visita_tec tt
					where	tt.ie_parecer		= 'S'
					and	tt.nr_sequencia		= hh.nr_seq_tipo_historico
					and	hh.dt_atualizacao	> dt_visita_w
					and	hh.nm_usuario		= a.nm_usuario_auditor
					and	hh.nr_seq_visita	= nr_seq_visita_p)
		order by 1) alias5;
BEGIN
select	coalesce(max(dt_fim_visita), pls_util_pck.obter_dt_vigencia_default('I'))
into STRICT	dt_visita_w
from	pls_visita_tecnica_hist
where	nr_seq_visita	= nr_seq_visita_p;

for c01_w in C01 loop
	begin
	if (c01_w.nr_linha = 1) then
		ds_auditores_w	:= substr(c01_w.nm_auditor,1,500);
	elsif (c01_w.nr_linha = c01_w.qt_linha) then
		ds_auditores_w	:= substr((ds_auditores_w||' '||obter_desc_expressao(312342)||' '||c01_w.nm_auditor),1,500);
	else
		ds_auditores_w	:= substr((ds_auditores_w||', '||c01_w.nm_auditor),1,500);
	end if;
	end;
end loop;

return ds_auditores_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
