CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.obter_perm_visita_operadora ( nr_seq_visita_p pls_visita_tecnica.nr_sequencia%type, nr_seq_visita_form_p pls_visita_tecnica_form.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_permite_iniciar_p INOUT text, ie_permite_finalizar_p INOUT text, ie_permite_editar_p INOUT text) AS $body$
DECLARE


ie_permite_iniciar_w varchar(1)	:= 'N';
ie_permite_finalizar_w varchar(1)	:= 'N';
ie_permite_editar_w varchar(1)		:= 'N';


BEGIN

select	coalesce(max('S'), 'N')
into STRICT	ie_permite_iniciar_w
from	pls_visita_tecnica_hist vh,
	pls_visita_tecnica v,
	pls_visita_tecnica_form f
where	v.nr_sequencia		= vh.nr_seq_visita
and	v.nr_sequencia		= f.nr_seq_visita
and	f.nr_sequencia		= nr_seq_visita_form_p
and	vh.ie_status_agendamento	= 2
and	f.ie_status	= 1
and	f.ie_situacao	= 'A'
and	f.ie_responsavel	= 'O';

select	coalesce(max('S'), 'N')
into STRICT	ie_permite_finalizar_w
from	pls_visita_tecnica v,
	pls_visita_tecnica_auditor a,
	pls_visita_tecnica_form f
where	v.nr_sequencia	= a.nr_seq_visita
and	v.nr_sequencia	= f.nr_seq_visita
and	f.nr_sequencia	= nr_seq_visita_form_p
and	a.nm_usuario_auditor = nm_usuario_p
and	a.ie_auditor_responsavel = 'S'
and	f.ie_status		= 1
and	v.ie_status		= 6
and	f.ie_responsavel	= 'O';

select	coalesce(max('S'), 'N')
into STRICT	ie_permite_editar_w
from	pls_visita_tecnica v,
	pls_visita_tecnica_form f
where	v.nr_sequencia	= f.nr_seq_visita
and	f.nr_sequencia	= nr_seq_visita_form_p
and	v.ie_status	= 6
and	f.ie_status	= 1
and	f.ie_situacao	= 'A'
and	f.ie_responsavel	= 'O';

ie_permite_iniciar_p	:= ie_permite_iniciar_w;
ie_permite_finalizar_p	:= ie_permite_finalizar_w;
ie_permite_editar_p	:= ie_permite_editar_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
