CREATE OR REPLACE PROCEDURE pls_visita_tecnica_prest_pck.recusar_prazo_solicitado ( nr_seq_historico_p pls_visita_tecnica_hist.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

nr_seq_visita_w		pls_visita_tecnica_inco.nr_seq_visita%type;
ds_inconsistencia_w	pls_visita_tecnica_inco.ds_inconsistencia%type;
dt_prazo_w		pls_visita_tecnica_inco.dt_limite%type;
dt_prazo_solicitado_w	pls_visita_tecnica_hist.dt_prazo_solicitado%type;

BEGIN

begin
select	b.nr_seq_visita,
	b.ds_inconsistencia,
	b.dt_limite,
	a.dt_prazo_solicitado
into STRICT	nr_seq_visita_w,
	ds_inconsistencia_w,
	dt_prazo_w,
	dt_prazo_solicitado_w
from	pls_visita_tecnica_hist	a,
	pls_visita_tecnica_inco	b
where	b.nr_sequencia		= a.nr_seq_inconsistencia
and	a.nr_sequencia		= nr_seq_historico_p
and	a.ie_status_prazo_inconsistencia = 1 --Pendente
and	b.ie_status		= 2; --Aguardando retorno;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
end;

update	pls_visita_tecnica_hist
set	ie_status_prazo_inconsistencia = 3,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_historico_p;

CALL CALL pls_visita_tecnica_prest_pck.gravar_log_sistema(	nr_seq_visita_p	=> nr_seq_visita_w,
				ie_tipo_log_p	=> 14,
				ds_log_p	=> wheb_mensagem_pck.get_texto(1213029,'DT_PRAZO='||to_char(dt_prazo_w,'dd/mm/yyyy')||';DT_PRAZO_SOLICITADO='||to_char(dt_prazo_solicitado_w,'dd/mm/yyyy')||';DS_INCONSISTENCIA='||ds_inconsistencia_w),
				nm_usuario_p	=> nm_usuario_p,
				ie_commit_p	=> 'N');

CALL CALL pls_visita_tecnica_prest_pck.gerar_alerta_evento(	nr_seq_visita_p		=> nr_seq_visita_w,
			ie_tipo_processo_p	=> 13,
			nm_usuario_p		=> nm_usuario_p,
			ie_commit_p		=> 'N');

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
