CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_assoc_proc ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

						
	dt_horario_w		timestamp;
	dt_fim_w		timestamp;
						
	
	c01 REFCURSOR;						
		
	tb_datas_w	dbms_sql.date_table;
		
	
BEGIN
	dt_fim_w := dt_validade_limite_p + 5;
	
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	
		open	c01 for
		SELECT	t.dt_horario
		from	(SELECT	/*+ LEADING(a) */
						c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	proc_interno w,
						prescr_procedimento y,
						prescr_material x,
						prescr_mat_hor c,
						prescr_medica a
				where	w.nr_sequencia = y.nr_seq_proc_interno
				and		y.nr_prescricao = x.nr_prescricao
				and		y.nr_sequencia = x.nr_sequencia_proc
				and		y.nr_prescricao = a.nr_prescricao
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_material
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.nr_atendimento = nr_atendimento_p
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		w.ie_tipo <> 'G'
				and		w.ie_tipo <> 'BS'
				and		coalesce(w.ie_ctrl_glic,'NC') = 'NC'
				and		(y.nr_seq_proc_interno IS NOT NULL AND y.nr_seq_proc_interno::text <> '')
				and		coalesce(y.nr_seq_prot_glic::text, '') = ''
				and		coalesce(y.nr_seq_exame::text, '') = ''
				and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(y.nr_seq_derivado::text, '') = ''
				and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(x.nr_seq_kit::text, '') = ''
				and		x.ie_agrupador = 5
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		c.ie_agrupador = 5
				and		coalesce(w.ie_apresentar_assoc,'N')	= 'S'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'IA',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario
		
union all

		select 	t.dt_horario
		from	(select	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				 from	prescr_procedimento y,
						prescr_material x,
						prescr_mat_hor c,
						prescr_medica a
				where	y.nr_prescricao = x.nr_prescricao
				and		y.nr_sequencia = x.nr_sequencia_proc
				and		y.nr_prescricao = a.nr_prescricao
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_material
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.nr_atendimento = nr_atendimento_p
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		coalesce(y.nr_seq_proc_interno::text, '') = ''
				and		coalesce(y.nr_seq_exame::text, '') = ''
				and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(y.nr_seq_derivado::text, '') = ''
				and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		coalesce(x.nr_seq_kit::text, '') = ''
				and		x.ie_agrupador = 5
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		c.ie_agrupador = 5
				and		coalesce(x.ie_checar_adep,'N')	= 'S'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'IA',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario;	
	
	else
		open	c01 for
		SELECT	t.dt_horario
		from 	(SELECT	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	proc_interno w,
						prescr_procedimento y,
						prescr_material x,
						prescr_mat_hor c,
						prescr_medica a
				where	w.nr_sequencia = y.nr_seq_proc_interno
				and		y.nr_prescricao = x.nr_prescricao
				and		y.nr_sequencia = x.nr_sequencia_proc
				and		y.nr_prescricao = a.nr_prescricao
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_material
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		w.ie_tipo <> 'G'
				and		w.ie_tipo <> 'BS'
				and		coalesce(w.ie_ctrl_glic,'NC') = 'NC'
				and		(y.nr_seq_proc_interno IS NOT NULL AND y.nr_seq_proc_interno::text <> '')
				and		coalesce(y.nr_seq_prot_glic::text, '') = ''
				and		coalesce(y.nr_seq_exame::text, '') = ''
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(y.nr_seq_derivado::text, '') = ''
				and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(x.nr_seq_kit::text, '') = ''
				and		x.ie_agrupador = 5
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		c.ie_agrupador = 5
				and		coalesce(w.ie_apresentar_assoc,'N')	= 'S'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'IA',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario
		
union all

		select	t.dt_horario
		from	(select	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	prescr_procedimento y,
						prescr_material x,
						prescr_mat_hor c,
						prescr_medica a
				where	y.nr_prescricao = x.nr_prescricao
				and		y.nr_sequencia = x.nr_sequencia_proc
				and		y.nr_prescricao = a.nr_prescricao
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_material
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)				
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		coalesce(y.nr_seq_proc_interno::text, '') = ''
				and		coalesce(y.nr_seq_exame::text, '') = ''
				and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(y.nr_seq_derivado::text, '') = ''
				and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(x.nr_seq_kit::text, '') = ''
				and		x.ie_agrupador = 5	
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		c.ie_agrupador = 5
				and		coalesce(x.ie_checar_adep,'N')	= 'S'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where 	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'IA',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario;	
	end if;
	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
