CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_coleta ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

						
	dt_horario_w	timestamp;
	dt_fim_w	timestamp;

	
	c01 REFCURSOR;	
		
	tb_datas_w	dbms_sql.date_table;	
	ie_dep_int_w	varchar(1);
	
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;

	ie_dep_int_w	:= plt_gerar_horarios_pck.get_ie_visualiza_dep_inte();
	
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	t.dt_horario
		from	(SELECT	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	prescr_procedimento x,
						prescr_proc_hor c,
						prescr_medica a
				where	x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_procedimento
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.nr_atendimento = nr_atendimento_p		
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		((a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w)or (a.ie_hemodialise IS NOT NULL AND a.ie_hemodialise::text <> ''))	
				and		coalesce(x.nr_seq_proc_interno::text, '') = ''
				and		(x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '')
				and		coalesce(x.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(x.nr_seq_derivado::text, '') = ''
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		coalesce(x.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(c.ie_situacao,'A') = 'A'
				and (ie_dep_int_w = 'S' or coalesce(x.nr_seq_origem::text, '') = '')
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LAB',
																						wheb_usuario_pck.get_cd_estabelecimento, 
																						wheb_usuario_pck.get_cd_setor_atendimento, 
																						wheb_usuario_pck.get_cd_perfil, 
																						null, 
																						c.cd_procedimento, 
																						c.ie_origem_proced, 
																						c.nr_seq_proc_interno,
																						a.cd_setor_Atendimento, 
																						x.cd_setor_Atendimento,
																						a.nr_prescricao,
																						x.nr_seq_exame) = 'S'))) -- nr_seq_exame_p
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'L',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario	 
		
union all

		select	t.dt_horario
		from	(select	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	proc_interno w,
						prescr_procedimento x,
						prescr_proc_hor c,
						prescr_medica a
				where	w.nr_sequencia = x.nr_seq_proc_interno
				and		w.nr_sequencia = c.nr_seq_proc_interno
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_procedimento
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.nr_atendimento = nr_atendimento_p		
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		((a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w)or (a.ie_hemodialise IS NOT NULL AND a.ie_hemodialise::text <> ''))		
				and		coalesce(w.ie_tipo,'N') <> 'G'
				and		coalesce(w.ie_tipo,'N') <> 'BS'
				and		coalesce(w.ie_ivc,'N') <> 'S'
				and		coalesce(w.ie_ctrl_glic,'XPTO') <> 'CIG'
				and		(x.nr_seq_proc_interno IS NOT NULL AND x.nr_seq_proc_interno::text <> '')
				and		coalesce(x.nr_seq_prot_glic::text, '') = ''
				and		(x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '')
				and		coalesce(x.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(x.nr_seq_derivado::text, '') = ''
				and		coalesce(x.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(c.ie_situacao,'A') = 'A'
				and (ie_dep_int_w = 'S' or coalesce(x.nr_seq_origem::text, '') = '')
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LAB', 
																						wheb_usuario_pck.get_cd_estabelecimento, 
																						wheb_usuario_pck.get_cd_setor_atendimento, 
																						wheb_usuario_pck.get_cd_perfil, 
																						null, 
																						c.cd_procedimento, 
																						c.ie_origem_proced, 
																						c.nr_seq_proc_interno,
																						a.cd_setor_Atendimento, 
																						x.cd_setor_Atendimento,
																						a.nr_prescricao,
																						x.nr_seq_exame) = 'S')))	  -- nr_seq_exame_p
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'L',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario;	
	else
		open	c01 for
		SELECT	t.dt_horario
		from	(SELECT	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	prescr_procedimento x,
						prescr_proc_hor c,
						prescr_medica a
				where	x.nr_prescricao = c.nr_prescricao
				and	x.nr_sequencia = c.nr_seq_procedimento
				and	x.nr_prescricao = a.nr_prescricao
				and	c.nr_prescricao = a.nr_prescricao
				and	a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
				and	a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and	((a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w)or (a.ie_hemodialise IS NOT NULL AND a.ie_hemodialise::text <> ''))	
				and	coalesce(x.nr_seq_proc_interno::text, '') = ''
				and	(x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '')
				and	coalesce(x.nr_seq_solic_sangue::text, '') = ''
				and	coalesce(x.nr_seq_derivado::text, '') = ''
				and	coalesce(x.nr_seq_exame_sangue::text, '') = ''
				and	coalesce(c.ie_situacao,'A') = 'A'
				and	PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and (ie_dep_int_w = 'S' or coalesce(x.nr_seq_origem::text, '') = '')
				and	((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'S') or
					((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LAB',
																					wheb_usuario_pck.get_cd_estabelecimento, 
																					wheb_usuario_pck.get_cd_setor_atendimento, 
																					wheb_usuario_pck.get_cd_perfil, 
																					null, 
																					c.cd_procedimento, 
																					c.ie_origem_proced, 
																					c.nr_seq_proc_interno,
																					a.cd_setor_Atendimento, 
																					x.cd_setor_Atendimento,
																					a.nr_prescricao,
																					x.nr_seq_exame) = 'S')))	  -- nr_seq_exame_p
				and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'L',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario	 
		
union all

		select	t.dt_horario
		from	(select	c.dt_horario,
						x.nr_sequencia,
						x.nr_prescricao,
						x.dt_suspensao
				from	proc_interno w,
						prescr_procedimento x,
						prescr_proc_hor c,
						prescr_medica a
				where	w.nr_sequencia = x.nr_seq_proc_interno
				and		w.nr_sequencia = c.nr_seq_proc_interno
				and		x.nr_prescricao = c.nr_prescricao
				and		x.nr_sequencia = c.nr_seq_procedimento
				and		x.nr_prescricao = a.nr_prescricao
				and		c.nr_prescricao = a.nr_prescricao
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		((a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w)or (a.ie_hemodialise IS NOT NULL AND a.ie_hemodialise::text <> ''))	
				and		coalesce(w.ie_tipo,'N') <> 'G'
				and		coalesce(w.ie_tipo,'N') <> 'BS'
				and		coalesce(w.ie_ivc,'N') <> 'S'
				and		coalesce(w.ie_ctrl_glic,'XPTO') <> 'CIG'
				and		(x.nr_seq_proc_interno IS NOT NULL AND x.nr_seq_proc_interno::text <> '')
				and		coalesce(x.nr_seq_prot_glic::text, '') = ''
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		(x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '')
				and		coalesce(x.nr_seq_solic_sangue::text, '') = ''
				and		coalesce(x.nr_seq_derivado::text, '') = ''
				and		coalesce(x.nr_seq_exame_sangue::text, '') = ''
				and		coalesce(c.ie_situacao,'A') = 'A'
				and (ie_dep_int_w = 'S' or coalesce(x.nr_seq_origem::text, '') = '')
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LAB', 
																						wheb_usuario_pck.get_cd_estabelecimento, 
																						wheb_usuario_pck.get_cd_setor_atendimento, 
																						wheb_usuario_pck.get_cd_perfil, 
																						null, 
																						c.cd_procedimento, 
																						c.ie_origem_proced, 
																						c.nr_seq_proc_interno,
																						a.cd_setor_Atendimento, 
																						x.cd_setor_Atendimento,
																						a.nr_prescricao,
																						x.nr_seq_exame) = 'S')))	  -- nr_seq_exame_p
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
							dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'L',
							t.nr_sequencia,			t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_horario;		
	end if;
	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;
	END;

$body$
