SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_edicao_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text, ie_prescr_usuario_p text, ie_estendidos_p bigint, dt_referencia_p timestamp) AS $body$
DECLARE

					
	dt_horario_w	timestamp;
	dt_fim_w	timestamp;
					
	
	c01 REFCURSOR;
	
	tb_datas_w	dbms_sql.date_table;
	
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	t.dt_status
		from	(SELECT	x.dt_status,
						x.nr_seq_solucao,
						x.nr_prescricao,
						x.dt_suspensao
				from	hd_prescricao b,
						prescr_solucao x,
						prescr_medica a
				where	x.nr_prescricao = a.nr_prescricao
				and     b.nr_sequencia  = x.nr_seq_dialise	
				and		a.nr_atendimento = nr_atendimento_p				
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_inicio_prescr	between dt_validade_limite_p	and dt_fim_w	
				and     b.ie_tipo_dialise in ('H','P')
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'DI',
																							cd_estabelecimento_p, 
																							cd_setor_usuario_p, 
																							cd_perfil_p, 
																							null, 
																							null, 
																							null, 
																							null,
																							a.cd_setor_Atendimento,
																							null,
																							null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																							null) = 'S'))) -- nr_seq_exame_p
				and		(x.nr_seq_dialise IS NOT NULL AND x.nr_seq_dialise::text <> '')) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_status,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'DI',
							t.nr_seq_solucao,		t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_status;	
	else
		open	c01 for
		SELECT	t.dt_status
		from	(SELECT	x.dt_status,
						x.nr_seq_solucao,
						x.nr_prescricao,
						x.dt_suspensao
				from	hd_prescricao b,
						prescr_solucao x,
						prescr_medica a
				where	x.nr_prescricao = a.nr_prescricao
				and     b.nr_sequencia  = x.nr_seq_dialise	
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
				and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_inicio_prescr	between dt_validade_limite_p	and dt_fim_w	
				and 	b.ie_tipo_dialise in ('H','P')
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'DI',
																							cd_estabelecimento_p, 
																							cd_setor_usuario_p, 
																							cd_perfil_p, 
																							null, 
																							null, 
																							null, 
																							null,
																							a.cd_setor_Atendimento,
																							null,
																							null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																							null) = 'S'))) -- nr_seq_exame_p
				and		(x.nr_seq_dialise IS NOT NULL AND x.nr_seq_dialise::text <> '')) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,		get_ie_plano_por_setor,		dt_plano_p,		t.dt_status,
							dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
							dt_referencia_p,		ie_edicao_p,		ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'DI',
							t.nr_seq_solucao,		t.nr_prescricao,	'H') = 'S'
		group by
			t.dt_status;
	end if;
	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;

	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_edicao_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text, ie_prescr_usuario_p text, ie_estendidos_p bigint, dt_referencia_p timestamp) FROM PUBLIC;
