SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_interv ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

						
	dt_horario_w	timestamp;
	dt_fim_w	timestamp;
	
	
	c01 REFCURSOR;
		
	tb_datas_w	dbms_sql.date_table;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	t.dt_horario
		from	(SELECT	c.dt_horario,
						x.nr_sequencia,
						a.nr_sequencia nr_pe_prescricao,
						x.dt_suspensao
				from	pe_prescr_proc x,
						pe_prescr_proc_hor c,
						pe_prescricao a
				where	x.nr_seq_prescr = c.nr_seq_pe_prescr
				and		x.nr_sequencia = c.nr_seq_pe_proc
				and		x.nr_seq_prescr = a.nr_sequencia
				and		c.nr_seq_pe_prescr = a.nr_sequencia
				and		a.nr_atendimento = nr_atendimento_p
				and		a.nr_sequencia	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sae_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sae_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_intervencao_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_intervencao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SAE',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								null, 
																								null,
																								null,
																								null) = 'S')))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			t.dt_horario,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		null,
											null,						'N',					ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'E',
											t.nr_sequencia,				t.nr_pe_prescricao,		'H') = 'S'
		group by
			t.dt_horario;	
	else
		open	c01 for
		SELECT	t.dt_horario
		from	(SELECT	c.dt_horario,
						x.nr_sequencia,
						a.nr_sequencia nr_pe_prescricao,
						x.dt_suspensao
				from	pe_prescr_proc x,
						pe_prescr_proc_hor c,
						pe_prescricao a
				where	x.nr_seq_prescr = c.nr_seq_pe_prescr
				and		x.nr_sequencia = c.nr_seq_pe_proc
				and		x.nr_seq_prescr = a.nr_sequencia
				and		c.nr_seq_pe_prescr = a.nr_sequencia
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
				and		a.nr_sequencia	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sae_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sae_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
				and		coalesce(c.ie_situacao,'A') = 'A'
				and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
				and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_intervencao_w')::char(1) = 'S') or
						((current_setting('plt_gerar_horarios_pck.ie_regra_incl_intervencao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SAE',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								null, 
																								null,
																								null, 
																								null) = 'S')))) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			t.dt_horario,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		null,
											null,						'N',					ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'E',
											t.nr_sequencia,				t.nr_pe_prescricao,		'H') = 'S'
		group by
			t.dt_horario;		
	end if;
	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_interv ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
