CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_jejum ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

						
	dt_horario_w		timestamp;
	dt_fim_w		timestamp;
	
	
	c01 REFCURSOR;

	tb_datas_w	dbms_sql.date_table;
	
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	t.dt_inicio
		from (SELECT	c.dt_inicio,
						c.ie_suspenso,
						c.nr_sequencia,
						c.nr_prescricao
				from	rep_jejum c,
						prescr_medica a
				where	c.nr_prescricao 			= a.nr_prescricao
				and		a.nr_atendimento 			= nr_atendimento_p
				and		a.nr_prescricao				between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
				and		a.dt_validade_prescr		between dt_validade_limite_p	and dt_fim_w) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,	dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,									t.dt_inicio,
											dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,								ie_estendidos_p,
											dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	CASE WHEN t.ie_suspenso='S' THEN  clock_timestamp()  ELSE null END ,	'J',
											t.nr_sequencia,			t.nr_prescricao,		'H') = 'S'
		group by t.dt_inicio
		
union all

		select	CASE WHEN to_char(t.dt_fim, 'mi')='00' THEN  t.dt_fim - 1/86400  ELSE t.dt_fim END
		from (select	c.dt_fim,
						c.ie_suspenso,
						c.nr_sequencia,
						c.nr_prescricao
				from	rep_jejum c,
						prescr_medica a
				where	c.nr_prescricao 			= a.nr_prescricao
				and		a.nr_atendimento 			= nr_atendimento_p
				and		a.nr_prescricao				between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
				and		a.dt_validade_prescr		between dt_validade_limite_p	and dt_fim_w) t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,									t.dt_fim,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,								ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	CASE WHEN t.ie_suspenso='S' THEN  clock_timestamp()  ELSE null END ,	'J',
											t.nr_sequencia,				t.nr_prescricao,		'H') = 'S'
		group by t.dt_fim
		
union all

		select	trunc(clock_timestamp(),'hh24')
		from (select	c.dt_fim,
						c.ie_suspenso,
						c.nr_sequencia,
						c.nr_prescricao
				from	rep_jejum c,
						prescr_medica a
				where	c.nr_prescricao 			= a.nr_prescricao
				and		a.nr_atendimento 			= nr_atendimento_p
				and		a.nr_prescricao				between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr		between dt_validade_limite_p	and dt_fim_w	
				and		not(c.dt_inicio 			between dt_inicial_horarios_p and dt_final_horarios_p)
				and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S') t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,									t.dt_fim,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,								ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	CASE WHEN t.ie_suspenso='S' THEN  clock_timestamp()  ELSE null END ,	'J',
											t.nr_sequencia,				t.nr_prescricao,		'H') = 'S'
		group by trunc(clock_timestamp(),'hh24');	
	else
		open	c01 for
		SELECT	t.dt_inicio
		from (SELECT	c.dt_inicio,
						c.ie_suspenso,
						c.nr_sequencia,
						c.nr_prescricao
				from	rep_jejum c,
						prescr_medica a
				where	c.nr_prescricao = a.nr_prescricao
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w
				and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S') t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,									t.dt_inicio,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,								ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	CASE WHEN t.ie_suspenso='S' THEN clock_timestamp()  ELSE null END ,		'J',
											t.nr_sequencia,				t.nr_prescricao,		'H') = 'S'
		group by
			t.dt_inicio
		
union all

		select	trunc(clock_timestamp(),'hh24')
		from (select	c.dt_fim,
						c.ie_suspenso,
						c.nr_sequencia,
						c.nr_prescricao
				from	rep_jejum c,
						prescr_medica a
				where	c.nr_prescricao = a.nr_prescricao
				and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)				
				and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
				and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
				and		not(c.dt_inicio between dt_inicial_horarios_p and dt_final_horarios_p)
				and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S') t
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,								t.dt_fim,
											dt_inicial_horarios_p,		dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,							ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	CASE WHEN t.ie_suspenso='S' THEN clock_timestamp()  ELSE null END ,	'J',
											t.nr_sequencia,				t.nr_prescricao,		'H') = 'S'
		group by
			trunc(clock_timestamp(),'hh24');		
	end if;
	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_jejum ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
