CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_npt_adulta ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

	
	dt_horario_w		timestamp;
	dt_fim_w			timestamp;
	
	
	c01 REFCURSOR;
	
	tb_datas_w	dbms_sql.date_table;
	
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;

	if (current_setting('plt_gerar_horarios_pck.ie_regra_incl_npt_adulta_w')::char(1) not in ('S','R')) then
		return;
	end if;
	
	-- Restricoes padroes do WHERE

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		a.nr_atendimento = ' || nr_atendimento_p || ' ', false);
	else
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		a.cd_pessoa_fisica = ''' || current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10) || ''' ', false);
	end if;	
	
	if (current_setting('plt_gerar_horarios_pck.ie_regra_incl_npt_adulta_w')::char(1) = 'R') then
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		(adep_obter_regra_inclusao(	''NAN'',
																							''' || cd_estabelecimento_p || ''',
																							''' || cd_setor_usuario_p || ''',
																							''' || cd_perfil_p || ''',
																							null,
																							null,
																							null,
																							null,
																							a.cd_Setor_atendimento,
																							null,
																							null,
																							null) = ''S'') ', false); -- nr_seq_exame_p. Passei nulo para o nr_prescricao_p (param acima) porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
	end if;
	
	-- Criacao da consulta SELECT

	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_w', ' select	dt_horario ' ||
	' from	(' ||
			'	select	x.dt_status dt_horario ' ||
			'	from		nut_pac x, ' ||
			'				prescr_medica a ' ||
			'	where		x.nr_prescricao = a.nr_prescricao ' ||
			'	and		a.dt_validade_prescr between ' ||
							' to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'')  ' ||
			'	and		x.dt_status between ' ||
							' to_date(''' || to_char(dt_inicial_horarios_p,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(dt_final_horarios_p,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'')  ' ||
			'	and		not exists(	select 1 from nut_paciente_hor z where z.nr_seq_nut_protocolo = x.nr_sequencia ) ' ||
			'	and		nvl(x.ie_acm,''N'') = ''N'' ' ||
			'	and		a.nr_prescricao	between ' || current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint || ' and ' || current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint ||
			'	and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = ''S'' ' ||
			current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) ||
			'	group by	x.dt_status ' ||
			' union all ' ||
			'	select	b.dt_horario dt_horario ' ||
			'	from	nut_paciente_hor b, ' ||
			'			nut_pac x, ' ||
			'			prescr_medica a	 ' ||
			'	where	x.nr_prescricao = a.nr_prescricao ' ||
			'	and		b.nr_seq_nut_protocolo	= x.nr_sequencia ' ||
			'	and		a.dt_validade_prescr between ' ||
							' to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss') || ''',''dd/mm/yyyy hh24:mi:ss'')  ' ||
			'	and		nvl(b.ie_horario_especial,''N'') = ''N'' ' ||
			'	and		a.nr_prescricao	between ' || current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint || ' and ' || current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint ||
			'	and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = ''S'' ' ||
			'	and		plt_obter_se_item_periodo(	to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || get_ie_plano_por_setor || ''', '||
													' ''' || dt_plano_p || ''', ' ||
													' b.dt_horario, ' ||
													' to_date(''' || to_char(dt_inicial_horarios_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(dt_final_horarios_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_prescr_usuario_p || ''', '||
													' ''' || nm_usuario_p || ''', '||
													' ''' || ie_estendidos_p || ''', ' ||
													' to_date(''' || to_char(dt_referencia_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_edicao_p || ''', ' ||
													' ''' || ie_exibir_hor_suspensos_p || ''', ' || 
													' x.dt_suspensao, ' || 
													' ''NAN'', ' || 
													' x.nr_sequencia, ' || 
													' a.nr_prescricao, ' || 
													' ''O'' ) = ''S'' ' ||
			current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) ||
			'	group by	b.dt_horario ' ||
			') ' ||
	' group by dt_horario ', false);
		
	open c01 for EXECUTE current_setting('plt_gerar_horarios_pck.sql_c01_w')::varchar(20000);	
	loop
	fetch C01 bulk collect into tb_datas_w
	limit 100;
	exit when tb_datas_w.count = 0;
		begin
		forall i in tb_datas_w.first..tb_datas_w.last
			
			insert into w_rep_horarios_t(	nm_usuario,
											dt_horario)
										values (
											nm_usuario_p,
											tb_datas_w(i));
			commit;
		end;
	end loop;
	close c01;
	
	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_w', null, false);
	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', null, false);
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
