CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_suspensos_p text) AS $body$
DECLARE

					
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp	timestamp;
	dt_fim_w	timestamp;	
						
	c01 CURSOR FOR
	SELECT	c.dt_horario
	from	prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a
	where	c.nr_seq_ordem = b.nr_sequencia
	and		b.nr_prescricao = a.nr_prescricao
	and		a.nr_atendimento = nr_atendimento_p
	and		a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and		c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
	and		((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and		coalesce(c.ie_horario_especial,'N') = 'N'
	group by
		c.dt_horario;

	
BEGIN
	if (get_ie_apresenta_ordem_med = 'S') then
		dt_fim_w	:= dt_validade_limite_p + 5;
		open c01;
		loop
		fetch c01 into current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			insert into w_rep_horarios_t(
				nm_usuario,
				dt_horario)
			values (
				nm_usuario_p,
				current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp);
				commit;
			end;
		end loop;
		close c01;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
