CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_formato_solucoes_p text, ie_prescr_usuario_p text, ie_sol_vigentes_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

						
	dt_horario_w		timestamp;
	dt_fim_w		timestamp;
						
	
	c01 REFCURSOR;
	c02 REFCURSOR;
		
	tb_datas_w	dbms_sql.date_table;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	t.dt_status
		from	(	SELECT	x.dt_status,
							x.nr_seq_solucao,
							x.nr_prescricao,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_medica a
					where	x.nr_prescricao = a.nr_prescricao
					and		a.nr_atendimento = nr_atendimento_p
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sol_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sol_w')::bigint	
					and		coalesce(x.nr_seq_dialise::text, '') = ''
					and		x.ie_status in ('P','N')
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SOL',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))		
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		((ie_sol_vigentes_p	= 'N') or (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or (exists (	select	1 
										from	prescr_mat_hor b
										where	b.nr_prescricao = x.nr_prescricao
										and		b.nr_seq_solucao = x.nr_seq_solucao
										and		b.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)))) t
		where	plt_obter_se_item_periodo(	null,					null,					get_ie_plano_por_setor,		dt_plano_p,			t.dt_status,
											dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'SOL',
											t.nr_seq_solucao,		t.nr_prescricao,		'H') = 'S'
		group by
			t.dt_status;
			
		open	c02 for
		SELECT	t.dt_horario
		from	(	SELECT	c.dt_horario,
							x.nr_seq_solucao,
							x.nr_prescricao,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_mat_hor c,
							prescr_medica a
					where	x.nr_prescricao = c.nr_prescricao
					and		x.nr_seq_solucao = c.nr_seq_solucao
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.nr_atendimento = nr_atendimento_p
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sol_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sol_w')::bigint	
					and		coalesce(x.nr_seq_dialise::text, '') = ''
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 4
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SOL',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null, 
																								null) = 'S')))		
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (x.ie_etapa_especial = 'S'))	
					and		adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S'
					and		((ie_sol_vigentes_p	= 'N') or (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or (exists (	select	1 
										from	prescr_mat_hor b
										where	b.nr_prescricao = x.nr_prescricao
										and		b.nr_seq_solucao = x.nr_seq_solucao
										and		b.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)))) t
		where	plt_obter_se_item_periodo(	null,					null,					get_ie_plano_por_setor,		dt_plano_p,		t.dt_horario,
										dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,	ie_estendidos_p,
										dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'SOL',
										t.nr_seq_solucao,		t.nr_prescricao,		'H') = 'S'
		group by
			t.dt_horario;	
	else
		open	c01 for
		SELECT	t.dt_status
		from	(	SELECT	x.dt_status,
							x.nr_seq_solucao,
							x.nr_prescricao,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_medica a
					where	x.nr_prescricao = a.nr_prescricao
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sol_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sol_w')::bigint	
					and		coalesce(x.nr_seq_dialise::text, '') = ''
					and		x.ie_status in ('P','N')
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SOL',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))						
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		((ie_sol_vigentes_p	= 'N') or (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or (exists (	select	1 
										from	prescr_mat_hor b
										where	b.nr_prescricao = x.nr_prescricao
										and		b.nr_seq_solucao = x.nr_seq_solucao
										and		b.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)))) t
		where	plt_obter_se_item_periodo(	null,					null,					get_ie_plano_por_setor,		dt_plano_p,			t.dt_status,
											dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'SOL',
											t.nr_seq_solucao,		t.nr_prescricao,		'H') = 'S'
		group by
			t.dt_status;
			
		open	c02 for
		SELECT	t.dt_horario
		from	(	SELECT	c.dt_horario,
							x.nr_seq_solucao,
							x.nr_prescricao,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_mat_hor c,
							prescr_medica a
					where	x.nr_prescricao = c.nr_prescricao
					and		x.nr_seq_solucao = c.nr_seq_solucao
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sol_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sol_w')::bigint	
					and		coalesce(x.nr_seq_dialise::text, '') = ''
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 4
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'S') or
							 ((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SOL',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))					
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (x.ie_etapa_especial = 'S'))	
					and		adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S'
					and		((ie_sol_vigentes_p	= 'N') or (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or (exists (	select	1 
										from	prescr_mat_hor b
										where	b.nr_prescricao = x.nr_prescricao
										and		b.nr_seq_solucao = x.nr_seq_solucao
										and		b.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)))) t
		where	plt_obter_se_item_periodo(	null,					null,					get_ie_plano_por_setor,		dt_plano_p,			t.dt_horario,
											dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'SOL',
											t.nr_seq_solucao,		t.nr_prescricao,		'H') = 'S'
		group by
			t.dt_horario;		
	end if;

	if (ie_formato_solucoes_p = 'I') then
		begin

		loop
		fetch C01 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				
				insert into w_rep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c01;
		end;
	elsif (ie_formato_solucoes_p = 'H') then
		begin

		loop
		fetch C02 bulk collect into tb_datas_w
		limit 100;
		exit when tb_datas_w.count = 0;
			begin
			forall i in tb_datas_w.first..tb_datas_w.last
				
				insert into w_rep_horarios_t(	nm_usuario,
												dt_horario)
											values (
												nm_usuario_p,
												tb_datas_w(i));
				commit;
			end;
		end loop;
		close c02;
		end;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_horarios_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_formato_solucoes_p text, ie_prescr_usuario_p text, ie_sol_vigentes_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
