SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_material ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_agrupar_acm_sn_p text, ie_prescr_usuario_p text, nr_seq_regra_p bigint, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE


	nr_seq_wadep_w			bigint;
	nr_prescricao_w			bigint;
	nr_seq_material_w		integer;
	cd_material_w			integer;
	ds_material_w			varchar(255);
	ie_acm_sn_w				varchar(1);
	cd_intervalo_w			varchar(7);
	qt_dose_w				double precision;
	ds_prescricao_w			varchar(100);
	ie_status_w				varchar(1);
	current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)		varchar(1);
	ie_liberado_w			varchar(1);
	cd_unid_med_qtde_w		varchar(30);
	ie_via_administracao_w	varchar(5);
	ds_interv_prescr_w		varchar(240);
	ie_erro_w				integer;
	ie_copiar_w				varchar(1);
	dt_fim_w				timestamp;
	dt_extensao_w			timestamp;
	hr_prim_horario_w		varchar(5);
	ie_plano_atual_w		varchar(1);
	ie_horario_susp_w		varchar(1);
	ie_retrogrado_w			varchar(1);
	ie_pend_assinatura_w 	varchar(1);
	ie_estendido_w			varchar(1);
	dt_validade_prescr_w	timestamp;
	ds_cor_titulo_w			varchar(20);
	ie_curta_duracao_w		varchar(1);
	cd_funcao_origem_w		bigint;
	cd_classif_setor_w		integer;
	ie_interv_farm_w        varchar(1);
	ie_recem_nato_w			varchar(1);
	ie_administrar_w		varchar(1);
	
	
	c01 REFCURSOR;

	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ie_copiar_w 	:= plt_obter_se_item_marcado('MAT', nr_seq_regra_p);
	ds_cor_titulo_w	:= plt_obter_cor_titulo('MAT',nr_seq_regra_p);

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
				CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END ,	
				cd_material,
				ds_material,
				ie_acm_sn,	
				cd_intervalo,
				qt_dose,
				ds_prescricao,
				CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  ie_status,
				cd_unidade_medida_dose,
				ie_via_aplicacao,
				ds_intervalo,
				ie_lib_pend_rep,
				ie_liberado,
				ie_erro,
				dt_extensao,
				hr_prim_horario,
				ie_plano_atual,
				ie_horario_susp,
				ie_retrogrado,
				ie_pend_assinatura,
				ie_estendido,
				dt_validade_prescr,
				ie_curta_duracao,
				cd_funcao_origem,
				ie_prescr_farm,
				ie_recem_nato,
				ie_administrar
		from	(	SELECT	nr_prescricao,
							nr_seq_material,
							cd_material,
							ds_material,
							ie_acm_sn,	
							cd_intervalo,
							qt_dose,
							ds_prescricao,
							ie_suspenso,
							cd_unidade_medida_dose,
							ie_via_aplicacao,
							ds_intervalo,
							ie_lib_pend_rep,
							ie_liberado,
							ie_erro,
							dt_extensao,
							hr_prim_horario,
							ie_plano_atual,
							ie_horario_susp,
							ie_retrogrado,
							ie_pend_assinatura,
							ie_estendido,
							dt_validade_prescr,
							ie_curta_duracao,
							cd_funcao_origem,
							ie_prescr_farm,
							ie_recem_nato,
							ie_administrar
					from	(	select	a.nr_prescricao,
										c.nr_seq_material,
										c.cd_material,
										y.ds_material,
										obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
										x.cd_intervalo,
										x.qt_dose,
										substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
										coalesce(x.ie_suspenso,'N') ie_suspenso,
										substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30) cd_unidade_medida_dose,
										x.ie_via_aplicacao,
										obter_desc_intervalo_prescr(x.cd_intervalo)||CASE WHEN coalesce(x.cd_intervalo::text, '') = '' THEN ''  ELSE ' ' END ||obter_desc_acm_sn(x.ie_acm,x.ie_se_necessario) ds_intervalo,
										substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) ie_lib_pend_rep,
										plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia) ie_liberado,
										CASE WHEN plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia)='N' THEN x.ie_erro  ELSE null END  ie_erro,
										PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade) dt_extensao,
										x.hr_prim_horario,
										substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1) ie_plano_atual,
										CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END  ie_horario_susp,
										coalesce(a.ie_prescr_emergencia,'N') ie_retrogrado,
										substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1) ie_pend_assinatura,
										substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1) ie_estendido,
										a.dt_validade_prescr,
										obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type) ie_curta_duracao,
										a.cd_funcao_origem,
										coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
										coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
										c.dt_horario,
										x.dt_suspensao,
										x.ie_administrar
								from	material y,
										prescr_material x,
										prescr_mat_hor c,
										prescr_medica a
								where	y.cd_material = x.cd_material
								and		x.nr_prescricao = c.nr_prescricao
								and		x.nr_sequencia = c.nr_seq_material	
								and		x.nr_prescricao = a.nr_prescricao
								and		c.nr_prescricao	= a.nr_prescricao	
								and		a.nr_atendimento = nr_atendimento_p
								and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
								and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
								and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w		
								and		x.ie_agrupador = 2
								and		coalesce(x.nr_seq_kit::text, '') = ''
								and		x.ie_origem_inf	<> 'K'
								and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'S') or
										((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'MAT',
																										cd_estabelecimento_p, 
																										cd_setor_usuario_p, 
																										cd_perfil_p, 
																										x.cd_material, 
																										null, 
																										null, 
																										null, 
																										a.cd_setor_Atendimento, 
																										null,
																										a.nr_prescricao,
																										null) = 'S'))) -- nr_seq_exame_p
								and		coalesce(c.ie_situacao,'A') = 'A'
								and		c.ie_agrupador = 2
								group by
										a.nr_prescricao,
										c.nr_seq_material,
										c.cd_material,
										y.ds_material,
										x.ie_acm,
										x.ie_se_necessario,		
										x.cd_intervalo,
										x.qt_dose,
										substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),
										x.ie_suspenso,
										substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30),
										x.ie_via_aplicacao,
										a.dt_liberacao_medico,
										a.dt_liberacao,
										a.dt_liberacao_farmacia,
										x.ie_erro,
										PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),
										x.hr_prim_horario,
										CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END ,
										a.ie_prescr_emergencia,
										substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1),
										substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1),
										a.dt_validade_prescr,
										obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type),
										a.cd_funcao_origem,
										a.ie_prescr_farm,
										coalesce(a.ie_recem_nato, 'N'),
										c.dt_horario,
										x.dt_suspensao,
										x.ie_administrar) t

					where	plt_obter_se_item_periodo(	dt_validade_limite_p,	dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			t.dt_horario,
														dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
														dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'MAT',
														t.nr_seq_material,		t.nr_prescricao,		'O') = 'S'
					
union all


					select	nr_prescricao,
							nr_seq_material,
							cd_material,
							ds_material,
							ie_acm_sn,	
							cd_intervalo,
							qt_dose,
							ds_prescricao,
							ie_suspenso,
							cd_unidade_medida_dose,
							ie_via_aplicacao,
							ds_intervalo,
							ie_lib_pend_rep,
							ie_liberado,
							ie_erro,
							dt_extensao,
							hr_prim_horario,
							ie_plano_atual,
							ie_horario_susp,
							ie_retrogrado,
							ie_pend_assinatura,
							ie_estendido,
							dt_validade_prescr,
							ie_curta_duracao,
							cd_funcao_origem,
							ie_prescr_farm,
							ie_recem_nato,
							ie_administrar
					from	(	select	a.nr_prescricao,
										x.nr_seq_material,
										x.cd_material,
										y.ds_material,
										obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
										x.cd_intervalo,
										x.qt_dose,
										substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,x.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
										coalesce(x.ie_suspenso,'N') ie_suspenso,
										substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30) cd_unidade_medida_dose,
										x.ie_via_aplicacao,
										obter_desc_intervalo_prescr(x.cd_intervalo)||CASE WHEN coalesce(x.cd_intervalo::text, '') = '' THEN ''  ELSE ' ' END ||obter_desc_acm_sn(x.ie_acm,x.ie_se_necessario) ds_intervalo,
										substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) ie_lib_pend_rep,
										plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia) ie_liberado,
										CASE WHEN plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia)='N' THEN x.ie_erro  ELSE null END  ie_erro,
										PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade) dt_extensao,
										x.hr_prim_horario,
										substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1) ie_plano_atual,
										CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END  ie_horario_susp,
										coalesce(a.ie_prescr_emergencia,'N') ie_retrogrado,
										substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1) ie_pend_assinatura,
										substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1) ie_estendido,
										a.dt_validade_prescr,
										obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type) ie_curta_duracao,
										a.cd_funcao_origem,
										coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
										coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
										x.dt_suspensao,
										x.ie_administrar
								from	material y,
										prescr_material x,
										prescr_medica a
								where	y.cd_material = x.cd_material
								and		x.nr_prescricao = a.nr_prescricao
								and		a.nr_atendimento = nr_atendimento_p
								and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
								and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
								and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w		
								and		x.ie_agrupador = 2
								and		coalesce(x.nr_seq_kit::text, '') = ''
								and		x.ie_origem_inf	<> 'K'
								and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'S') or
										((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'MAT', 
																										cd_estabelecimento_p, 
																										cd_setor_usuario_p, 
																										cd_perfil_p, 
																										x.cd_material, 
																										null, 
																										null, 
																										null, 
																										a.cd_setor_Atendimento, 
																										null,
																										a.nr_prescricao,
																										null) = 'S'))) -- nr_seq_exame_p
                                and		((not exists (	select	1
														from	prescr_mat_hor c
														where	c.nr_prescricao = x.nr_prescricao
														and		c.nr_seq_material = x.nr_sequencia
														and		c.cd_material = x.cd_material
														and		coalesce(c.ie_situacao,'A') = 'A')) or (exists (	select	1
													from 	prescr_mat_hor c
													where	c.nr_prescricao = x.nr_prescricao
													and 	c.nr_seq_material = x.nr_sequencia
													and 	c.cd_material = x.cd_material
													and 	Obter_ocorrencia_intervalo(x.cd_intervalo,a.nr_horas_validade,'H') > 24
													and		obter_operacao_intervalo(x.cd_intervalo) not in ('X', 'F', 'V')
													and 	c.dt_horario not between dt_inicial_horarios_p and dt_final_horarios_p
													and		get_ie_plano_por_setor <> 'R')))
								and		(((coalesce(x.ie_administrar,'S')	= 'N') or (coalesce(a.ie_emergencia,'N') 	= 'S') or (Obter_ocorrencia_intervalo(x.cd_intervalo,a.nr_horas_validade,'H') > 24)) and (obter_operacao_intervalo(x.cd_intervalo) not in ('X', 'F', 'V') or (coalesce(a.ie_emergencia,'N') = 'S')))
								group by
										a.nr_prescricao,
										x.nr_seq_material,
										x.cd_material,
										y.ds_material,
										x.ie_acm,
										x.ie_se_necessario,		
										x.cd_intervalo,
										x.qt_dose,
										substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),
										x.ie_suspenso,
										substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30),
										x.ie_via_aplicacao,
										a.dt_liberacao_medico,
										a.dt_liberacao,
										a.dt_liberacao_farmacia,
										x.ie_erro,
										PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),
										x.hr_prim_horario,
										CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END ,
										a.ie_prescr_emergencia,
										substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1),
										substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1),
										a.dt_validade_prescr,
										obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type),
										a.cd_funcao_origem,
										a.ie_prescr_farm,
										coalesce(a.ie_recem_nato, 'N'),
										x.dt_suspensao,
										x.ie_administrar) alias155) alias156
		group by
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END ,	
			cd_material,
			ds_material,
			ie_acm_sn,	
			cd_intervalo,
			qt_dose,
			ds_prescricao,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END ,
			cd_unidade_medida_dose,
			ie_via_aplicacao,
			ds_intervalo,
			ie_lib_pend_rep,
			ie_liberado,
			ie_erro,
			dt_extensao,
			hr_prim_horario,
			ie_plano_atual,
			ie_horario_susp,
			ie_retrogrado,
			ie_pend_assinatura,
			ie_estendido,
			dt_validade_prescr,
			ie_curta_duracao,
			cd_funcao_origem,
			ie_prescr_farm,
			ie_recem_nato,
			ie_administrar;
	else
		open	c01 for
		SELECT	CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
				CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END ,	
				cd_material,
				ds_material,
				ie_acm_sn,	
				cd_intervalo,
				qt_dose,
				ds_prescricao,
				CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  ie_status,
				cd_unidade_medida_dose,
				ie_via_aplicacao,
				ds_intervalo,
				ie_lib_pend_rep,
				ie_liberado,
				ie_erro,
				dt_extensao,
				hr_prim_horario,
				ie_plano_atual,
				ie_horario_susp,
				ie_retrogrado,
				ie_pend_assinatura,
				ie_estendido,
				dt_validade_prescr,
				ie_curta_duracao,
				cd_funcao_origem,
				ie_prescr_farm,
				ie_recem_nato,
				ie_administrar
		from	(
			SELECT	nr_prescricao,
					nr_seq_material,
					cd_material,
					ds_material,
					ie_acm_sn,	
					cd_intervalo,
					qt_dose,
					ds_prescricao,
					ie_suspenso,
					cd_unidade_medida_dose,
					ie_via_aplicacao,
					ds_intervalo,
					ie_lib_pend_rep,
					ie_liberado,
					ie_erro,
					dt_extensao,
					hr_prim_horario,
					ie_plano_atual,
					ie_horario_susp,
					ie_retrogrado,
					ie_pend_assinatura,
					ie_estendido,
					dt_validade_prescr,
					ie_curta_duracao,
					cd_funcao_origem,
					ie_prescr_farm,
					ie_recem_nato,
					ie_administrar
			from	(	select	a.nr_prescricao,
								c.nr_seq_material,
								c.cd_material,
								y.ds_material,
								obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
								x.cd_intervalo,
								x.qt_dose,
								substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
								coalesce(x.ie_suspenso,'N') ie_suspenso,
								substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30) cd_unidade_medida_dose,
								x.ie_via_aplicacao,
								obter_desc_intervalo_prescr(x.cd_intervalo)||CASE WHEN coalesce(x.cd_intervalo::text, '') = '' THEN ''  ELSE ' ' END ||obter_desc_acm_sn(x.ie_acm,x.ie_se_necessario) ds_intervalo,
								substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) ie_lib_pend_rep,
								plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia) ie_liberado,
								CASE WHEN plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia)='N' THEN x.ie_erro  ELSE null END  ie_erro,
								PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade) dt_extensao,
								x.hr_prim_horario,
								substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1) ie_plano_atual,
								CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END  ie_horario_susp,
								coalesce(a.ie_prescr_emergencia,'N') ie_retrogrado,
								substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1) ie_pend_assinatura,
								substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1) ie_estendido,
								a.dt_validade_prescr,
								obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type) ie_curta_duracao,
								a.cd_funcao_origem,
								coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
								coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
								c.dt_horario,
								x.dt_suspensao,
								x.ie_administrar
						from	material y,
								prescr_material x,
								prescr_mat_hor c,
								prescr_medica a
						where	y.cd_material = x.cd_material
						and		x.nr_prescricao = c.nr_prescricao
						and		x.nr_sequencia = c.nr_seq_material	
						and		x.nr_prescricao = a.nr_prescricao
						and		c.nr_prescricao	= a.nr_prescricao	
						and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)			
						and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
						and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
						and		a.dt_validade_prescr between dt_validade_limite_p	and dt_fim_w		
						and		x.ie_agrupador = 2
						and		coalesce(x.nr_seq_kit::text, '') = ''
						and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'S') or
								((current_setting('plt_gerar_horarios_pck.ie_regra_incl_material_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'MAT',
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								x.cd_material, 
																								null, 
																								null, 
																								null, 
																								a.cd_setor_Atendimento, 
																								null,
																								a.nr_prescricao,
																								null) = 'S')))	  -- nr_seq_exame_p
						and		x.ie_origem_inf	<> 'K'
						and		coalesce(c.ie_situacao,'A') = 'A'
						and		c.ie_agrupador = 2
						group by
								a.nr_prescricao,
								c.nr_seq_material,
								c.cd_material,
								y.ds_material,
								x.ie_acm,
								x.ie_se_necessario,		
								x.cd_intervalo,
								x.qt_dose,
								substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),
								x.ie_suspenso,
								substr(obter_desc_unid_med(obter_unid_med_usua(x.cd_unidade_medida_dose)),1,30),
								x.ie_via_aplicacao,
								a.dt_liberacao_medico,
								a.dt_liberacao,
								a.dt_liberacao_farmacia,
								x.ie_erro,
								PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),
								x.hr_prim_horario,
								CASE WHEN get_ie_identifica_hor_susp='S' THEN coalesce(x.ie_horario_susp,'N')  ELSE null END ,
								a.ie_prescr_emergencia,
								substr(obter_pendencia_assinatura(nm_usuario_p,a.nr_prescricao,'PR'),1,1),
								substr(PLT_obter_ie_validade(PLT_obter_dt_extensao(x.dt_extensao,a.dt_validade_prescr,a.nr_horas_validade),a.dt_validade_prescr,dt_referencia_p),1,1),
								a.dt_validade_prescr,
								obter_ie_curta_duracao(a.nr_prescricao,dt_referencia_p, current_setting('plt_gerar_horarios_pck.cd_perfil_w')::perfil.cd_perfil%type, current_setting('plt_gerar_horarios_pck.cd_pessoa_usuario_w')::pessoa_fisica.cd_pessoa_fisica%type, current_setting('plt_gerar_horarios_pck.cd_especialidade_w')::especialidade_medica.cd_especialidade%type),
								a.cd_funcao_origem,
								a.ie_prescr_farm,
								coalesce(a.ie_recem_nato, 'N'),
								c.dt_horario,
								x.dt_suspensao,
								x.ie_administrar) t
			where	plt_obter_se_item_periodo(	dt_validade_limite_p,	dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			t.dt_horario,
												dt_inicial_horarios_p,	dt_final_horarios_p,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
												dt_referencia_p,		ie_edicao_p,			ie_exibir_hor_suspensos_p,	t.dt_suspensao,		'MAT',
												t.nr_seq_material,		t.nr_prescricao,		'O') = 'S') alias71
		group by
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END ,	
			cd_material,
			ds_material,
			ie_acm_sn,	
			cd_intervalo,
			qt_dose,
			ds_prescricao,
			CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END ,
			cd_unidade_medida_dose,
			ie_via_aplicacao,
			ds_intervalo,
			ie_lib_pend_rep,
			ie_liberado,
			ie_erro,
			dt_extensao,
			hr_prim_horario,
			ie_plano_atual,
			ie_horario_susp,
			ie_retrogrado,
			ie_pend_assinatura,
			ie_estendido,
			dt_validade_prescr,
			ie_curta_duracao,
			cd_funcao_origem,
			ie_prescr_farm,
			ie_recem_nato,
			ie_administrar;

	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			cd_unid_med_qtde_w,
			ie_via_administracao_w,
			ds_interv_prescr_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			ie_liberado_w,
			ie_erro_w,
			dt_extensao_w,
			hr_prim_horario_w,
			ie_plano_atual_w,
			ie_horario_susp_w,
			ie_retrogrado_w,
			ie_pend_assinatura_w,
			ie_estendido_w,
			dt_validade_prescr_w,
			ie_curta_duracao_w,
			cd_funcao_origem_w,
			ie_interv_farm_w,
			ie_recem_nato_w,
			ie_administrar_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		select	coalesce(Obter_classif_setor_atend(nr_atendimento_p),0)
		into STRICT	cd_classif_setor_w
		;
			
		if (ie_estendido_w	= 'P') and (coalesce(dt_extensao_w::text, '') = '') then
			dt_extensao_w	:= dt_validade_prescr_w;
		end if;	
		
		if (((ie_estendido_w = 'P') and
			 ((coalesce(cd_funcao_origem_w,924) <> 950) or (ie_curta_duracao_w = 'N'))) or (cd_classif_setor_w = 1)) then
			ie_estendido_w	:= 'N';
		end if;
		
		select	nextval('w_rep_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		
		insert into w_rep_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			nr_prescricao,
			nr_seq_item,		
			cd_item,
			ds_item,
			ie_acm_sn,		
			cd_intervalo,
			qt_item,
			ds_prescricao,
			ie_status_item,
			nr_agrupamento,
			nr_seq_proc_interno,
			ie_diferenciado,
			cd_unid_med_qtde,
			ie_via_aplicacao,
			ds_interv_prescr,
			ie_pendente_liberacao,
			ie_liberado,
			ie_erro,
			ie_copiar,
			dt_extensao,
			hr_prim_horario,
			ie_plano_atual,
			ie_horario_susp,
			ie_retrogrado,
			ie_pend_assinatura,
			dt_atualizacao,
			ie_estendido,
			ds_cor_titulo,
			ie_curta_duracao,
			ie_interv_farm,
			ie_item_rn,
			nr_prescricoes,
			ie_administrar)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'MAT',
			nr_prescricao_w,
			nr_seq_material_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			0,
			0,
			'N',
			cd_unid_med_qtde_w,
			ie_via_administracao_w,
			ds_interv_prescr_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			ie_liberado_w,
			ie_erro_w,
			CASE WHEN ie_status_w='S' THEN 'N'  ELSE ie_copiar_w END ,
			dt_extensao_w,
			hr_prim_horario_w,
			ie_plano_atual_w,
			ie_horario_susp_w,
			ie_retrogrado_w,
			ie_pend_assinatura_w,
			clock_timestamp(),
			ie_estendido_w,
			ds_cor_titulo_w,
			ie_curta_duracao_w,
			ie_interv_farm_w,
			ie_recem_nato_w,
			nr_prescricao_w,
			ie_administrar_w);
		end;
	end loop;
	close c01;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_material ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_agrupar_acm_sn_p text, ie_prescr_usuario_p text, nr_seq_regra_p bigint, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
