			dt_validade_prescr,
			nr_dia_util,
			ie_curta_duracao,
			cd_funcao_origem,
			ie_prescr_farm,
			ie_recem_nato,
			ie_prescr_anest,
			qt_solucao;
	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ds_dil_obs_w,
			ie_status_w,
			cd_unid_med_qtde_w,
			ie_via_administracao_w,
			ds_interv_prescr_w,
			ie_proximo_plano_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			ie_liberado_w,
			ie_erro_w,
			dt_extensao_w,
			hr_prim_horario_w,
			ie_administrar_w,
			ie_plano_atual_w,
			ie_horario_susp_w,
			ds_dose_diferenciada_w,
			ie_composto_w,
			ds_composto_w,
			ie_retrogrado_w,
			ie_quimio_w,
			ie_medicacao_paciente_w,
			ie_alto_risco_w,
			ds_medic_nao_padrao_w,
			ie_pend_assinatura_w,
			ds_observacao_w,
			ie_estendido_w,
			dt_validade_prescr_w,
			nr_dia_util_w,
			ie_curta_duracao_w,
			cd_funcao_origem_w,
			ie_interv_farm_w,
			ie_recem_nato_w,
			ie_prescr_anest_w,
			qt_solucao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		select	count(*)
		into STRICT	cont_w
		from	w_rep_t
		where	nm_usuario = nm_usuario_p
		and 	ie_tipo_item = 'M'
		and 	coalesce(nr_prescricao,coalesce(nr_prescricao_w,0)) = coalesce(nr_prescricao_w,0)
		and 	coalesce(nr_seq_item,coalesce(nr_seq_material_w,0)) = coalesce(nr_seq_material_w,0)
		and 	cd_item = cd_material_w
		and 	ds_item = ds_material_w
		and	ie_liberado = 'S'
		and	coalesce(ie_pendente_liberacao,'X') <> 'M'
		and 	coalesce(cd_intervalo,'0') = coalesce(cd_intervalo_w,'0')
		and 	coalesce(qt_item,0) = coalesce(qt_dose_w,0)
		and	coalesce(ie_composto,'N') = ie_composto_w
		and	coalesce(ie_retrogrado,'N') = ie_retrogrado_w
		and	coalesce(ie_status_item,'N') <> 'S'
		and	coalesce(hr_prim_horario,'XP') = coalesce(hr_prim_horario_w,'XP')
		and	coalesce(ds_medic_nao_padrao,'XPTO') = coalesce(ds_medic_nao_padrao_w,'XPTO')
		and	coalesce(ie_pend_assinatura,'N') = ie_pend_assinatura_w
		and	coalesce(ds_observacao,'XPTO')   = coalesce(ds_observacao_w,'XPTO')
		and	coalesce(nr_dia_util,0) = coalesce(nr_dia_util_w,0)
		and	coalesce(ie_interv_farm,'N') = coalesce(ie_interv_farm_W,'N')
		and	coalesce(ie_via_aplicacao,'N') = coalesce(ie_via_administracao_w,'N')
		and	coalesce(ie_item_rn,'N') = coalesce(ie_recem_nato_w,'N')
		and coalesce(qt_solucao,0) = coalesce(qt_solucao_w,0);

		if (cont_w > 0) and (coalesce(current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),'X') <> 'M') and (coalesce(ie_status_w,'X') <> 'S') then
			
			ds_comando_update_w	:=	' update w_rep_t ' ||
							' set ie_pendente_liberacao = substr(ie_pendente_liberacao || :ie_pendente_liberacao,1,2) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and ie_liberado = ''S'' '||
							' and nvl(ie_pendente_liberacao,''X'') <> ''M'' ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and nvl(nr_agrupamento,0) = nvl(:nr_agrupamento,0) ' ||
							' and nvl(ie_composto,''N'') = :ie_composto ' ||
							' and nvl(ie_retrogrado,''N'') = :ie_retrogrado ' ||
							' and nvl(ie_medicacao_paciente,''N'') = :ie_medic_paciente ' ||
							' and nvl(ds_medic_nao_padrao,''XPTO'') = nvl(:ds_medic_nao_padrao,''XPTO'') '||
							' and nvl(ie_pend_assinatura,''N'') = :ie_pend_assinatura '||
							' and nvl(ds_observacao,''XPTO'') = nvl(:ds_observacao,''XPTO'') ' ||
							' and nvl(nr_dia_util,0) = nvl(:nr_dia_util,0)' ||
							' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm' ||
							' and nvl(ie_via_aplicacao, ''N'') = :ie_via_aplicacao' ||
							' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ' ||
							' and nvl(qt_solucao,0) = nvl(:qt_solucao,0) ';
	
			CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=M' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||																
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'nr_agrupamento=' || to_char(nr_agrupamento_w) || ds_sep_bv_w ||
										'ie_composto=' || ie_composto_w  || ds_sep_bv_w ||
										'ie_retrogrado=' || ie_retrogrado_w || ds_sep_bv_w ||
										'ie_medic_paciente='||  ie_medicacao_paciente_w ||ds_sep_bv_w ||
										'ds_medic_nao_padrao='|| ds_medic_nao_padrao_w ||ds_sep_bv_w||
										'ie_pend_assinatura='||ie_pend_assinatura_w||ds_sep_bv_w||
										'ds_observacao='||ds_observacao_w||ds_sep_bv_w||
										'nr_dia_util='||nr_dia_util_w||ds_sep_bv_w ||
										'ie_interv_farm='||ie_interv_farm_w||ds_sep_bv_w||
										'ie_via_aplicacao='||ie_via_administracao_w||ds_sep_bv_w||
										'ie_recem_nato='||ie_recem_nato_w||ds_sep_bv_w||
										'qt_solucao='||coalesce(qt_solucao_w,0)||ds_sep_bv_w);

		else
			
			select	coalesce(Obter_classif_setor_atend(nr_atendimento_p),0)
			into STRICT	cd_classif_setor_w
			;
			
			if (ie_estendido_w	= 'P') and (coalesce(dt_extensao_w::text, '') = '') then
				dt_extensao_w	:= dt_validade_prescr_w;
			end if;
			
			if (((ie_estendido_w = 'P') and
				 ((coalesce(cd_funcao_origem_w,924) <> 950) or (ie_curta_duracao_w = 'N'))) or (cd_classif_setor_w = 1)) then
				ie_estendido_w	:= 'N';
			end if;	
		
			select	nextval('w_rep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			
			insert into w_rep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				nr_agrupamento,
				ds_diluicao,
				ie_status_item,
				nr_seq_proc_interno,
				ie_diferenciado,
				nr_prescricoes,
				cd_unid_med_qtde,
				ie_via_aplicacao,
				ds_interv_prescr,
				ie_proximo_plano,
				ie_pendente_liberacao,
				ie_liberado,
				ie_erro,
				ie_copiar,
				dt_extensao,
				hr_prim_horario,
				ie_administrar,
				ie_plano_atual,
				ie_horario_susp,
				ds_dose_diferenciada,
				ds_hint,
				ie_composto,
				ds_composto,
				ie_retrogrado,
				ie_quimio,
				ie_medicacao_paciente,
				ie_alto_risco,
				ds_medic_nao_padrao,
				ie_pend_assinatura,
				ds_observacao,
				dt_atualizacao,
				ie_estendido,
				ds_cor_titulo,
				nr_dia_util,
				ie_curta_duracao,
				ie_interv_farm,
				ie_item_rn,
				ie_prescricao_anestesica,
				qt_solucao)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'M',
				nr_prescricao_w,
				nr_seq_material_w,
				cd_material_w,
				ds_material_w,
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_dose_w,
				nr_agrupamento_w,
				ds_dil_obs_w,
				ie_status_w,
				0,
				CASE WHEN coalesce(ds_dil_obs_w::text, '') = '' THEN 'N'  ELSE 'S' END ,
				nr_prescricao_w,
				cd_unid_med_qtde_w,
				ie_via_administracao_w,
				ds_interv_prescr_w,
				ie_proximo_plano_w,
				current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_liberado_w,
				ie_erro_w,
				CASE WHEN ie_status_w='S' THEN 'N'  ELSE ie_copiar_w END ,
				dt_extensao_w,
				hr_prim_horario_w,
				ie_administrar_w,
				ie_plano_atual_w,
				ie_horario_susp_w,
				ds_dose_diferenciada_w,
				ds_dil_obs_w,
				ie_composto_w,
				ds_composto_w,
				ie_retrogrado_w,
				ie_quimio_w,
				ie_medicacao_paciente_w,
				ie_alto_risco_w,
				ds_medic_nao_padrao_w,
				ie_pend_assinatura_w,
				ds_observacao_w,
				clock_timestamp(),
				ie_estendido_w,
				ds_cor_titulo_w,
				nr_dia_util_w,
				ie_curta_duracao_w,
				ie_interv_farm_w,
				ie_recem_nato_w,
				ie_prescr_anest_w,
				qt_solucao_w);
				
		end if;
		end;
	end loop;
	close c01;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_obter_medic ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_agrupar_acm_sn_p text, ie_prescr_usuario_p text, nr_seq_regra_p bigint, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
