CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_obter_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_suspensos_p text) AS $body$
DECLARE


	nr_seq_wadep_w		bigint;
	nr_prescricao_w		bigint;
	nr_seq_item_ordem_w	integer;
	nr_seq_ordem_w		bigint;
	nr_seq_horario_w	bigint;
	ds_ordem_w			varchar(255);
	ie_acm_sn_w			varchar(1);
	ds_prescricao_w		varchar(255);
	ds_diluicao_w		varchar(2000);
	dt_fim_w			timestamp;
	current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000)	varchar(255);
						
	c01 CURSOR FOR
	SELECT	nr_prescricao,
		nr_seq_item_ordem,
		nr_seq_ordem,
		nr_seq_horario,
		ds_ordem,
		ie_acm_sn,	
		ds_prescricao,
		ds_diluicao,
		nr_prescricoes
	from	(
		SELECT	a.nr_prescricao,
			b.nr_seq_solucao nr_seq_item_ordem,
			b.nr_sequencia nr_seq_ordem,
			c.nr_sequencia nr_seq_horario,
			x.ds_ordem,
			'N' ie_acm_sn,
			obter_desc_solucao_adep(a.nr_prescricao,b.nr_seq_solucao) ds_prescricao,
			b.ds_ordem ds_diluicao,
			CASE WHEN c.ie_horario_especial='S' THEN  a.nr_prescricao  ELSE null END  nr_prescricoes
		from	ordem_medica x,
			prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a
		where	x.nr_sequencia = b.nr_seq_ordem
		and	c.nr_seq_ordem = b.nr_sequencia
		and	b.nr_prescricao = a.nr_prescricao
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
		and	(b.nr_seq_solucao IS NOT NULL AND b.nr_seq_solucao::text <> '')
		and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
		group by
			a.nr_prescricao,
			b.nr_seq_solucao,
			b.nr_sequencia,
			c.nr_sequencia,
			x.ds_ordem,
			'N',
			b.ds_ordem,
			CASE WHEN c.ie_horario_especial='S' THEN  a.nr_prescricao  ELSE null END
		
union all

		select	a.nr_prescricao,
			b.nr_seq_material nr_seq_item_ordem,
			b.nr_sequencia nr_seq_ordem,
			c.nr_sequencia nr_seq_horario,
			x.ds_ordem,
			'N' ie_acm_sn,
			z.ds_material ds_prescricao,
			b.ds_ordem ds_diluicao,
			CASE WHEN c.ie_horario_especial='S' THEN  a.nr_prescricao  ELSE null END  nr_prescricoes
		from	material z,
			prescr_material y,
			ordem_medica x,
			prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a
		where	z.cd_material = y.cd_material
		and	y.nr_prescricao = a.nr_prescricao
		and	y.nr_prescricao = b.nr_prescricao
		and	y.nr_sequencia = b.nr_seq_material
		and	x.nr_sequencia = b.nr_seq_ordem
		and	c.nr_seq_ordem = b.nr_sequencia
		and	b.nr_prescricao = a.nr_prescricao
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p
		and	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
		and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
		group by
			a.nr_prescricao,
			b.nr_seq_material,
			b.nr_sequencia,
			c.nr_sequencia,
			x.ds_ordem,
			'N',
			z.ds_material,
			b.ds_ordem,
			CASE WHEN c.ie_horario_especial='S' THEN  a.nr_prescricao  ELSE null END 
		) alias11
	group by
		nr_prescricao,
		nr_seq_item_ordem,
		nr_seq_ordem,
		nr_seq_horario,
		ds_ordem,
		ie_acm_sn,	
		ds_prescricao,
		ds_diluicao,
		nr_prescricoes;

	
BEGIN
	if (get_ie_apresenta_ordem_med = 'S') then
		dt_fim_w	:= dt_validade_limite_p + 5;
		
		open c01;
		loop
		fetch c01 into	nr_prescricao_w,
				nr_seq_item_ordem_w,
				nr_seq_ordem_w,
				nr_seq_horario_w,
				ds_ordem_w,
				ie_acm_sn_w,
				ds_prescricao_w,
				ds_diluicao_w,
				current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000);
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			select	nextval('w_rep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			
			insert into w_rep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				ds_prescricao,
				ds_diluicao,
				cd_intervalo,
				ie_diferenciado,
				nr_dia_util,
				nr_seq_proc_interno,
				ie_medic_controlado,
				nr_agrupamento,
				qt_item,
				nr_prescricoes)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'B',
				nr_prescricao_w,
				nr_seq_item_ordem_w,
				nr_seq_horario_w,
				ds_ordem_w,
				ie_acm_sn_w,
				ds_prescricao_w,
				ds_diluicao_w,
				'X',
				'N',
				0,
				0,
				'N',
				0,
				0,
				current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000));

			commit;
			end;
		end loop;
		close c01;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
