LEFT OUTER JOIN prescr_mat_hor h ON (x.nr_prescricao = h.nr_prescricao AND x.nr_sequencia = h.nr_seq_material)
WHERE y.cd_material = x.cd_material and x.nr_prescricao = a.nr_prescricao   and a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10) and a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint and PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S' and a.dt_validade_prescr	between dt_validade_limite_p and dt_fim_w and x.ie_agrupador = 8 and ((current_setting('plt_gerar_horarios_pck.ie_regra_incl_sne_w')::char(1) = 'S') or
