CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_assoc_proc ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

					
	ds_sep_bv_w			varchar(50);
	nr_prescricao_w			bigint;
	nr_seq_material_w		integer;
	nr_seq_horario_w		bigint;
	ie_status_horario_w		varchar(15);
	cd_material_w			integer;
	ds_material_w			varchar(255);
	ie_acm_sn_w			varchar(1);
	cd_intervalo_w			varchar(7);
	qt_dose_w			double precision;
	nr_agrupamento_w		double precision;
	ie_controlado_w			varchar(1);
	ds_prescricao_w			varchar(100);
	ie_status_w			varchar(1);
	dt_suspensao_w			timestamp;
	ie_diferenciado_w		varchar(1);
	ds_dil_obs_w			varchar(2000);
	nr_dia_util_w			bigint;
	ds_comando_update_w		varchar(4000);
	current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)		varchar(1);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp			timestamp;
	nr_horario_w			integer;	
	dt_fim_w			timestamp;	
	ie_interv_farm_w		varchar(1);
	ie_recem_nato_w			varchar(1);

	
	c01 REFCURSOR;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ds_sep_bv_w	:= obter_separador_bv;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ds_prescricao,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				ie_prescr_farm,
				ie_recem_nato
		from	(	SELECT	/*+ LEADING(a) */
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							substr(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,c.dt_lib_horario),1,15) ie_status_horario,
							c.cd_material,
							z.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							x.qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao dt_suspensao,
							'N' ie_diferenciado,
							substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) ds_dil_obs,
							x.nr_dia_util,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.dt_suspensao dt_susp_item
					from	material z,
							proc_interno w,
							prescr_procedimento y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	z.cd_material = x.cd_material
					and		z.cd_material = c.cd_material
					and		w.nr_sequencia = y.nr_seq_proc_interno
					and		y.nr_prescricao = x.nr_prescricao
					and		y.nr_sequencia = x.nr_sequencia_proc
					and		y.nr_prescricao = a.nr_prescricao
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.nr_atendimento = nr_atendimento_p
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		w.ie_tipo <> 'G'
					and		w.ie_tipo <> 'BS'
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		coalesce(w.ie_ctrl_glic,'NC') = 'NC'
					and		(y.nr_seq_proc_interno IS NOT NULL AND y.nr_seq_proc_interno::text <> '')
					and		coalesce(y.nr_seq_prot_glic::text, '') = ''
					and		coalesce(y.nr_seq_exame::text, '') = ''
					and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
					and		coalesce(y.nr_seq_derivado::text, '') = ''
					and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		x.ie_agrupador = 5
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 5
					and		coalesce(w.ie_apresentar_assoc,'N')	= 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.ie_dose_especial,
							c.nr_seq_processo,
							c.nr_seq_area_prep,
							c.cd_material,
							z.ds_material,
							x.ie_acm,
							x.ie_se_necessario,		
							x.cd_intervalo,
							x.qt_dose,
							x.nr_agrupamento,
							c.ie_controlado,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							x.ie_suspenso,
							x.ds_observacao,
							x.nr_dia_util,
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							a.ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N'),
							x.dt_suspensao
					
union all

					select	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							SUBSTR(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,c.dt_lib_horario),1,15) ie_status_horario,
							c.cd_material,
							z.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							x.qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao dt_suspensao,
							'N' ie_diferenciado,
							substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) ds_dil_obs,
							x.nr_dia_util,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.dt_suspensao dt_susp_item
					from	material z,
							prescr_procedimento y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	z.cd_material = x.cd_material
					and		z.cd_material = c.cd_material
					and		y.nr_prescricao = x.nr_prescricao
					and		y.nr_sequencia = x.nr_sequencia_proc
					and		y.nr_prescricao = a.nr_prescricao
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.nr_atendimento = nr_atendimento_p			
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		coalesce(y.nr_seq_proc_interno::text, '') = ''
					and		coalesce(y.nr_seq_exame::text, '') = ''
					and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		coalesce(y.nr_seq_derivado::text, '') = ''
					and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		x.ie_agrupador = 5
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 5
					and		coalesce(x.ie_checar_adep,'N')	= 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
								a.nr_prescricao,
								c.nr_seq_material,
								c.nr_sequencia,
								c.dt_fim_horario,
								c.dt_suspensao,
								c.ie_dose_especial,
								c.nr_seq_processo,
								c.nr_seq_area_prep,
								c.cd_material,
								z.ds_material,
								x.ie_acm,
								x.ie_se_necessario,		
								x.cd_intervalo,
								x.qt_dose,
								coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
								x.nr_agrupamento,
								c.ie_controlado,
								x.ie_suspenso,
								x.ds_observacao,
								x.nr_dia_util,
								c.dt_lib_horario,
								a.dt_liberacao_medico,
								a.dt_liberacao,
								a.dt_liberacao_farmacia,
								c.dt_horario,
								a.ie_prescr_farm,
								coalesce(a.ie_recem_nato, 'N'),
								x.dt_suspensao) alias73
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'IA',
											nr_seq_material,			nr_prescricao,		'S') = 'S'
		group by		
				nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ds_prescricao,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				ie_prescr_farm,
				ie_recem_nato
		order by
				dt_suspensao;	
	else
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ds_prescricao,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				ie_prescr_farm,
				ie_recem_nato
		from	(	SELECT	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							SUBSTR(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,c.dt_lib_horario),1,15) ie_status_horario,
							c.cd_material,
							z.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							x.qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao dt_suspensao,
							'N' ie_diferenciado,
							substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) ds_dil_obs,
							x.nr_dia_util,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.dt_suspensao dt_susp_item
					from	material z,
							proc_interno w,
							prescr_procedimento y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	z.cd_material = x.cd_material
					and		z.cd_material = c.cd_material
					and		w.nr_sequencia = y.nr_seq_proc_interno
					and		y.nr_prescricao = x.nr_prescricao
					and		y.nr_sequencia = x.nr_sequencia_proc
					and		y.nr_prescricao = a.nr_prescricao
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		w.ie_tipo <> 'G'
					and		w.ie_tipo <> 'BS'
					and		coalesce(w.ie_ctrl_glic,'NC') = 'NC'
					and		(y.nr_seq_proc_interno IS NOT NULL AND y.nr_seq_proc_interno::text <> '')
					and		coalesce(y.nr_seq_prot_glic::text, '') = ''
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		coalesce(y.nr_seq_exame::text, '') = ''
					and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
					and		coalesce(y.nr_seq_derivado::text, '') = ''
					and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		x.ie_agrupador = 5
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 5
					and		coalesce(w.ie_apresentar_assoc,'N')	= 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.ie_dose_especial,
							c.nr_seq_processo,
							c.nr_seq_area_prep,
							c.cd_material,
							z.ds_material,
							x.ie_acm,
							x.ie_se_necessario,		
							x.cd_intervalo,
							x.qt_dose,
							x.nr_agrupamento,
							c.ie_controlado,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							x.ie_suspenso,
							x.ds_observacao,
							x.nr_dia_util,
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							a.ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N'),
							x.dt_suspensao
					
union all

					select	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							substr(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,c.dt_lib_horario),1,15) ie_status_horario,
							c.cd_material,
							z.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							x.qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao dt_suspensao,
							'N' ie_diferenciado,
							substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) ds_dil_obs,
							x.nr_dia_util,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.dt_suspensao dt_susp_item
					from	material z,
							prescr_procedimento y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	z.cd_material = x.cd_material
					and		z.cd_material = c.cd_material
					and		y.nr_prescricao = x.nr_prescricao
					and		y.nr_sequencia = x.nr_sequencia_proc
					and		y.nr_prescricao = a.nr_prescricao
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)			
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		coalesce(y.nr_seq_proc_interno::text, '') = ''
					and		coalesce(y.nr_seq_exame::text, '') = ''
					and		coalesce(y.nr_seq_solic_sangue::text, '') = ''
					and		coalesce(y.nr_seq_derivado::text, '') = ''
					and		coalesce(y.nr_seq_exame_sangue::text, '') = ''
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		x.ie_agrupador = 5
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 5
					and		coalesce(x.ie_checar_adep,'N')	= 'S'
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.ie_dose_especial,
							c.nr_seq_processo,
							c.nr_seq_area_prep,
							c.cd_material,
							z.ds_material,
							x.ie_acm,
							x.ie_se_necessario,		
							x.cd_intervalo,
							x.qt_dose,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							x.nr_agrupamento,
							c.ie_controlado,
							x.ie_suspenso,
							x.ds_observacao,
							x.nr_dia_util,
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							a.ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N'),
							x.dt_suspensao) alias76
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'IA',
											nr_seq_material,			nr_prescricao,		'S') = 'S'
		group by		
				nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ds_prescricao,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				ie_prescr_farm,
				ie_recem_nato
		order by
				dt_suspensao;		
	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ie_controlado_w,
			ds_prescricao_w,
			ie_status_w,
			dt_suspensao_w,
			ie_diferenciado_w,
			ds_dil_obs_w,
			nr_dia_util_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp,
			ie_interv_farm_w,
			ie_recem_nato_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then		
			ds_comando_update_w	:=	' update w_rep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ie_diferenciado = :ie_diferenciado, ' ||
							' ds_diluicao = :ds_dil_obs, ' ||
							' ds_hint = :ds_dil_obs, ' ||							
							' ie_medic_controlado = :ie_controlado, ' ||
							' nr_dia_util = :nr_dia_util ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
							' and nvl(nr_agrupamento,0) = nvl(:nr_agrupamento,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ' ||
							' and nvl(ie_status_item,''N'') = nvl(:ie_status,''N'') '||
							' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm' ||
							' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ';
						
			CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ie_diferenciado=' || ie_diferenciado_w || ds_sep_bv_w || 
										'ds_dil_obs=' || ds_dil_obs_w || ds_sep_bv_w || 
										'ie_controlado=' || ie_controlado_w || ds_sep_bv_w || 
										'nr_dia_util=' || to_char(nr_dia_util_w) || ds_sep_bv_w || 
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=IA' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||								
										'nr_agrupamento=' || to_char(nr_agrupamento_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'ie_status=' || ie_status_w || ds_sep_bv_w || 
										'ie_interv_farm='||ie_interv_farm_w||ds_sep_bv_w||
										'ie_recem_nato='||ie_recem_nato_w||ds_sep_bv_w);
		end if;								
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_assoc_proc ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
