							c.nr_seq_procedimento,
							c.nr_sequencia,
							substr(plt_obter_status_hor_proc_col(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.dt_lib_horario),1,15) ie_status_horario,
							c.cd_procedimento,
							substr(adep_obter_desc_exame_lab(x.ie_acm,x.ie_se_necessario,z.nm_exame)|| 
								CASE WHEN coalesce(x.cd_material_exame::text, '') = '' THEN null  ELSE ' - '||v.ds_material_exame END || 
								CASE WHEN coalesce(x.ds_material_especial::text, '') = '' THEN null  ELSE ' - ('||x.ds_material_especial||')' END ,1,240) ds_procedimento,
							c.nr_seq_proc_interno,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							x.qt_procedimento,
							v.ds_material_exame ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao dt_suspensao,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							x.nr_seq_exame,
							CASE WHEN coalesce(obter_se_mostra_adic(20),'S')='N' THEN null  ELSE substr(x.ds_observacao,1,255) END  ds_observacao,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.dt_suspensao dt_susp_item
					FROM exame_laboratorio z, procedimento y, proc_interno w, prescr_proc_hor c, prescr_medica a, prescr_procedimento x
LEFT OUTER JOIN material_exame_lab v ON (x.cd_material_exame = v.cd_material_exame)
WHERE z.nr_seq_exame = x.nr_seq_exame and y.cd_procedimento = x.cd_procedimento and y.ie_origem_proced = x.ie_origem_proced and y.cd_procedimento = c.cd_procedimento and y.ie_origem_proced = c.ie_origem_proced and w.nr_sequencia = x.nr_seq_proc_interno and w.nr_sequencia = c.nr_seq_proc_interno and x.nr_prescricao = c.nr_prescricao and x.nr_sequencia = c.nr_seq_procedimento and x.nr_prescricao = a.nr_prescricao and c.nr_prescricao = a.nr_prescricao and a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10) and PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S' and a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint and ((a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w)or (a.ie_hemodialise IS NOT NULL AND a.ie_hemodialise::text <> '')) and coalesce(w.ie_tipo,'N') <> 'G' and coalesce(w.ie_tipo,'N') <> 'BS' and coalesce(w.ie_ivc,'N') <> 'S' and coalesce(w.ie_ctrl_glic,'XPTO') <> 'CIG' and (ie_dep_int_w = 'S' or coalesce(x.nr_seq_origem::text, '') = '') and ((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_coleta_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LAB', 
																							wheb_usuario_pck.get_cd_estabelecimento, 
																							wheb_usuario_pck.get_cd_setor_atendimento, 
																							wheb_usuario_pck.get_cd_perfil, 
																							null, 
																							c.cd_procedimento, 
																							c.ie_origem_proced, 
																							c.nr_seq_proc_interno,
																							a.cd_setor_Atendimento, 
																							x.cd_setor_Atendimento,
																							a.nr_prescricao,
																							x.nr_seq_exame) = 'S'))) 	  -- nr_seq_exame_p
  and (x.nr_seq_proc_interno IS NOT NULL AND x.nr_seq_proc_interno::text <> '') and coalesce(x.nr_seq_prot_glic::text, '') = '' and (x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '') and coalesce(x.nr_seq_solic_sangue::text, '') = '' and coalesce(x.nr_seq_derivado::text, '') = '' and coalesce(x.nr_seq_exame_sangue::text, '') = '' and coalesce(c.ie_situacao,'A') = 'A' and ((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')) group by
							a.nr_prescricao,
							c.nr_seq_procedimento,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.ie_dose_especial,
							c.cd_procedimento,
							x.ie_acm,
							x.ie_se_necessario,		
							z.nm_exame,
							c.nr_seq_proc_interno,
							x.cd_intervalo,
							x.qt_procedimento,
							v.ds_material_exame,
							x.ie_suspenso,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							x.ds_material_especial,
							x.cd_material_exame,
							v.ds_material_exame,
							x.nr_seq_exame,
							x.ds_observacao,
							a.ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N'),
							x.dt_suspensao) alias109
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'L',
											nr_seq_procedimento,			nr_prescricao,		'S') = 'S'
		group by		
				nr_prescricao,
				nr_seq_procedimento,
				nr_sequencia,
				ie_status_horario,
				cd_procedimento,
				ds_procedimento,
				nr_seq_proc_interno,
				ie_acm_sn,
				cd_intervalo,
				qt_procedimento,
				ds_prescricao,
				ie_suspenso,
				dt_suspensao,
				ie_lib_pend_rep,
				dt_horario,
				nr_seq_exame,
				ds_observacao,
				ie_prescr_farm,
				ie_recem_nato
		order by
			dt_suspensao;		
	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_procedimento_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_procedimento_w,
			ds_procedimento_w,
			nr_seq_proc_interno_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_procedimento_w,
			ds_prescricao_w,
			ie_suspenso_w,
			dt_suspensao_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp,
			nr_seq_exame_w,
			ds_observacao_w,
			ie_interv_farm_w,
			ie_recem_nato_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then		
			ds_comando_update_w	:=	' update w_rep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' ds_diluicao = :ds_diluicao ,'||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and nvl(nr_seq_proc_interno,nvl(:nr_seq_proc_interno,0)) = nvl(:nr_seq_proc_interno,0) ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ie_status_item = :ie_status ' ||
							' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
							' and nvl(nr_seq_exame,0) = nvl(:nr_seq_exame,0) ' ||
							' and ds_item = :ds_item ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ' ||
							' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm ' ||
							' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ';
						
			CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ds_diluicao='|| ds_observacao_w || ds_sep_bv_w || 
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=' || ie_tipo_w || ds_sep_bv_w ||
										'nr_seq_item=' || to_char(nr_seq_procedimento_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_procedimento_w) || ds_sep_bv_w ||
										'nr_seq_proc_interno=' || to_char(nr_seq_proc_interno_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_procedimento_w) || ds_sep_bv_w ||
										'ie_status=' || ie_suspenso_w || ds_sep_bv_w ||
										'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||	
										'nr_seq_exame=' || to_char(nr_seq_exame_w) || ds_sep_bv_w ||											
										'ds_item=' || ds_procedimento_w || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'ie_interv_farm='|| ie_interv_farm_w || ds_sep_bv_w ||
										'ie_recem_nato='|| ie_recem_nato_w || ds_sep_bv_w );
		end if;								
		end;
	end loop;
	close c01;
	
	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p,current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'L', 'N',null);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_coleta ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
