SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_edicao_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text, ie_prescr_usuario_p text, ie_estendidos_p bigint, dt_referencia_p timestamp) AS $body$
DECLARE

				
	ds_sep_bv_w		varchar(50);				
	nr_prescricao_w		bigint;
	nr_seq_solucao_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	ds_comando_update_w	varchar(4000);
	nr_etapa_sol_w		bigint;
	dt_etapa_prev_w		timestamp;
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_w		timestamp;
	
	
	c01 REFCURSOR;
			
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	dt_fim_w	:= dt_validade_limite_p + 5;
	
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_solucao,
				ie_status,
				dt_status
		from	(	SELECT	a.nr_prescricao,
							x.nr_seq_solucao,
							CASE WHEN coalesce(x.ie_status,'P')='P' THEN  'W'  ELSE x.ie_status END  ie_status,
							x.dt_status,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_medica a,
							hd_prescricao b
					where	x.nr_prescricao   = a.nr_prescricao
					and     a.nr_prescricao   = b.nr_prescricao
					and     b.nr_sequencia    = x.nr_seq_dialise
					and     b.nr_prescricao   = a.nr_prescricao
					and		coalesce(x.ie_status,'X')	<> 'V'
					and     b.ie_tipo_dialise in ('H','P')
					and		(x.nr_seq_dialise IS NOT NULL AND x.nr_seq_dialise::text <> '')
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		a.nr_atendimento = nr_atendimento_p		
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'DI', 
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_inicio_prescr	between dt_validade_limite_p	and dt_fim_w	
					group by
							a.nr_prescricao,
							x.nr_seq_solucao,
							x.ie_status,
							x.dt_status,
							x.dt_suspensao) alias18
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_status,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_suspensao,		'DI',
											nr_seq_solucao,				nr_prescricao,			'S') = 'S'
		group by
				nr_prescricao,
				nr_seq_solucao,
				ie_status,
				dt_status
		order by 
				dt_status;	
	else
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_solucao,
				ie_status,
				dt_status
		from	(	SELECT	a.nr_prescricao,
							x.nr_seq_solucao,
							CASE WHEN coalesce(x.ie_status,'P')='P' THEN  'W'  ELSE x.ie_status END  ie_status,
							x.dt_status,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_medica a,
							hd_prescricao b
					where	x.nr_prescricao   = a.nr_prescricao
					and     a.nr_prescricao   = b.nr_prescricao
					and     b.nr_sequencia    = x.nr_seq_dialise
					and     b.nr_prescricao   = a.nr_prescricao
					and		coalesce(x.ie_status,'X')	<> 'V'
					and     b.ie_tipo_dialise in ('H','P')
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		(x.nr_seq_dialise IS NOT NULL AND x.nr_seq_dialise::text <> '')
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'S') or
							 ((current_setting('plt_gerar_horarios_pck.ie_regra_incl_dialise_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'DI', 
																								cd_estabelecimento_p, 
																								cd_setor_usuario_p, 
																								cd_perfil_p, 
																								null, 
																								null, 
																								null, 
																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_inicio_prescr	between dt_validade_limite_p	and dt_fim_w	
					group by
							a.nr_prescricao,
							x.nr_seq_solucao,
							x.ie_status,
							x.dt_status,
							x.dt_suspensao) alias19
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_status,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_suspensao,		'DI',
											nr_seq_solucao,				nr_prescricao,			'S') = 'S'
		group by
				nr_prescricao,
				nr_seq_solucao,
				ie_status,
				dt_status
		order by 
				dt_status;		
	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
					nr_seq_solucao_w,
					ie_status_horario_w,
					current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin		
		nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
		ds_comando_update_w	:=	' update w_rep_t ' ||
						' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
						' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
						' where nm_usuario = :nm_usuario ' ||
						' and ie_tipo_item = :ie_tipo ' ||
						' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
						' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ';
					
		CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
									'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
									'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
									'ie_tipo=DI' || ds_sep_bv_w ||
									'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w );
		end if;
		end;
	end loop;
	close c01;

	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p, current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'DIA', 'N',null);
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_edicao_p text, ie_exibir_hor_suspensos_p text, dt_plano_p text, ie_prescr_usuario_p text, ie_estendidos_p bigint, dt_referencia_p timestamp) FROM PUBLIC;
