		and	a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint		
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
		and	((c.dt_inicio between current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp and current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp) or (c.dt_fim between current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp and current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp))
		and	((a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '' AND ie_prescr_usuario_p = 'N') or (a.nm_usuario_original = nm_usuario_p))
		and	(((ie_estendidos_p = 0) and (PLT_obter_se_estendido(coalesce(c.dt_extensao,a.dt_validade_prescr),dt_referencia_p) = 'S')) or
			 ((ie_estendidos_p = 1) and (PLT_obter_se_estendido(coalesce(c.dt_extensao,a.dt_validade_prescr),dt_referencia_p) = 'N')) or (ie_estendidos_p = 2))
		and	(((ie_edicao_p = 'S') and (plt_obter_se_liberado(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia) = 'N')) or (ie_edicao_p = 'N'))		
		and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.ie_suspenso,'N') <> 'S'))
		group by
