			if (ie_status_horario_w	= 'S') then
				vl_hora_w	:= 'S' ||to_char(nr_sequencia_w) || 'H' || ie_status_horario_w;
			else	
				vl_hora_w	:= null;
			end if;	
			
			ds_comando_update_w	:=	' update w_rep_t ' ||
							' set hora'|| to_char(nr_horario_w) || ' = nvl(:vl_hora, hora'||to_char(nr_horario_w)||'),'||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
							' and cd_item = :cd_item ' ||
							' and ((ds_prescricao = :ds_vigencia) or (ds_prescricao is null)) ' ||
							' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm' ||
							' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ';
						
			CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=' || vl_hora_w || ds_sep_bv_w ||
