										'nr_seq_item='|| to_char(nr_sequencia_w) || ds_sep_bv_w ||
										'ie_tipo=J' || ds_sep_bv_w || 
										'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||
