SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_leite ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_agrupar_acm_sn_p text, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

					
	ds_sep_bv_w			varchar(50);
	nr_prescricao_w			bigint;
	nr_seq_material_w		integer;
	nr_seq_horario_w		bigint;
	ie_status_horario_w		varchar(1);
	cd_material_w			integer;
	ds_material_w			varchar(255);
	ie_acm_sn_w			varchar(1);
	cd_intervalo_w			varchar(7);
	qt_dose_w			double precision;
	ds_prescricao_w			varchar(100);
	ie_status_w			varchar(1);
	current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)		varchar(1);
	ds_comando_update_w		varchar(4000);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp			timestamp;
	nr_horario_w			integer;	
	dt_fim_w			timestamp;
	nr_seq_leite_deriv_w		bigint;
	ie_composto_w			varchar(1);

	
	c01 REFCURSOR;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ds_sep_bv_w	:= obter_separador_bv;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_hor,	
				cd_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				ds_prescricao,
				ie_suspenso,
				ie_lib_pend_rep,
				dt_horario,
				nr_seq_leite,
				ie_composto
		from	(	SELECT	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							SUBSTR(plt_obter_status_hor_supl_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) ie_status_hor,	
							c.cd_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							b.cd_intervalo,
							x.qt_dose,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							b.nr_sequencia nr_seq_leite,
							substr(coalesce(obter_se_leite_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),'N'),1,1) ie_composto,
							x.dt_suspensao dt_susp_item,
							c.dt_suspensao
					from	prescr_leite_deriv b,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	x.nr_prescricao		= c.nr_prescricao
					and		x.nr_sequencia		= c.nr_seq_material	
					and		x.nr_prescricao		= a.nr_prescricao
					and		c.nr_prescricao		= a.nr_prescricao	
					and		b.nr_prescricao		= x.nr_prescricao
					and		b.nr_sequencia		= x.nr_seq_leite_deriv
					and		a.nr_atendimento	= nr_atendimento_p		
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		x.ie_agrupador		= 16
					and		coalesce(c.ie_situacao,'A')	= 'A'
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_leite_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_leite_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LD',
																							cd_estabelecimento_p, 
																							cd_setor_usuario_p, 
																							cd_perfil_p, 
																							c.cd_material, 
																							null, 
																							null, 
																							null,
																							a.cd_Setor_atendimento,
																							null,
																							a.nr_prescricao,
																							null) = 'S')))	  -- nr_seq_exame_p
					and		c.ie_agrupador		= 16
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
						a.nr_prescricao,
						c.nr_seq_material,
						c.nr_sequencia,
						c.dt_fim_horario,
						c.dt_suspensao,
						c.cd_material,
						x.ie_acm,
						x.ie_se_necessario,		
						b.cd_intervalo,
						x.qt_dose,
						coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
						x.ie_suspenso,
						c.dt_lib_horario,
						a.dt_liberacao_medico,
						a.dt_liberacao,
						a.dt_liberacao_farmacia,
						c.dt_horario,
						b.nr_sequencia,
						obter_se_leite_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),
						x.dt_suspensao) alias37
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'LD',
											nr_seq_material,			nr_prescricao,			'S') = 'S'
		order by
				dt_horario,
				dt_suspensao;	
	else
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_hor,	
				cd_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				ds_prescricao,
				ie_suspenso,
				ie_lib_pend_rep,
				dt_horario,
				nr_seq_leite,
				ie_composto
		from	(	SELECT	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							SUBSTR(plt_obter_status_hor_supl_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario),1,1) ie_status_hor,	
							c.cd_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							b.cd_intervalo,
							x.qt_dose,
							substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
							b.nr_sequencia nr_seq_leite,
							substr(coalesce(obter_se_leite_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),'N'),1,1) ie_composto,
							x.dt_suspensao dt_susp_item,
							c.dt_suspensao
					from	prescr_leite_deriv b,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	x.nr_prescricao		= c.nr_prescricao
					and		x.nr_sequencia		= c.nr_seq_material	
					and		x.nr_prescricao		= a.nr_prescricao
					and		c.nr_prescricao		= a.nr_prescricao	
					and		b.nr_prescricao		= x.nr_prescricao
					and		b.nr_sequencia		= x.nr_seq_leite_deriv
					and		a.cd_pessoa_fisica	= current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint	and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p	and dt_fim_w	
					and		x.ie_agrupador		= 16
					and		coalesce(c.ie_situacao,'A')	= 'A'
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_leite_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_leite_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'LD',
																							cd_estabelecimento_p, 
																							cd_setor_usuario_p, 
																							cd_perfil_p, 
																							c.cd_material, 
																							null, 
																							null, 
																							null,
																							a.cd_Setor_atendimento,
																							null,
																							a.nr_prescricao,
																							null) = 'S')))
					and		c.ie_agrupador		= 16
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
					group by
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.cd_material,
							x.ie_acm,
							x.ie_se_necessario,		
							b.cd_intervalo,
							x.qt_dose,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							x.ie_suspenso,
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							b.nr_sequencia,
							obter_se_leite_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),
						x.dt_suspensao) alias38
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'LD',
											nr_seq_material,			nr_prescricao,			'S') = 'S'
		order by
				dt_horario,
				dt_suspensao;		
	end if;
	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp,
			nr_seq_leite_deriv_w,
			ie_composto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then

			ds_comando_update_w	:=	' update w_rep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_leite_deriv,nvl(:nr_seq_leite_deriv,0)) = nvl(:nr_seq_leite_deriv,0) ' ||							
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and nvl(ie_status_item,''N'') = :ie_status ' ||
							' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ' ||
							' and nvl(ie_composto,''N'') = :ie_composto ';
						
			CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=LD' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'nr_seq_leite_deriv='|| to_char(nr_seq_leite_deriv_w) || ds_sep_bv_w ||										
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'ie_status=' || to_char(ie_status_w) || ds_sep_bv_w ||
										'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||								
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'ie_composto=' || ie_composto_w || ds_sep_bv_w );

		end if;
		end;
	end loop;
	close c01;
	
	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p, current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'LD', 'N',null);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_leite ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_agrupar_acm_sn_p text, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
