CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_medic ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

					
	ds_sep_bv_w			varchar(50);
	nr_prescricao_w			bigint;
	nr_seq_material_w		integer;
	nr_seq_horario_w		bigint;
	ie_status_horario_w		varchar(15);
	cd_material_w			integer;
	ds_material_w			varchar(255);
	ie_acm_sn_w			varchar(1);
	cd_intervalo_w			varchar(7);
	qt_dose_w			double precision;
	nr_agrupamento_w		double precision;
	ie_controlado_w			varchar(1);
	ie_status_w			varchar(1);
	dt_suspensao_w			timestamp;
	ie_diferenciado_w		varchar(1);
	ds_dil_obs_w			varchar(2000);
	nr_dia_util_w			bigint;
	nr_dia_util_ww			bigint;
	ds_comando_update_w		varchar(4000);
	current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)		varchar(15);
	ie_lib_pend_rep_2w		varchar(15);
	hr_prim_horario_w		varchar(15);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp			timestamp;
	nr_horario_w			integer;	
	dt_fim_w			timestamp;
	dt_fim_horario_w		timestamp;
	nr_prescr_grid_w		bigint;
	nr_seq_grid_w			integer;
	nr_contagem_w			bigint;
	nr_Seq_teste_w			numeric(20);
	ie_composto_w			varchar(1);
	ie_plano_atual_w		varchar(1);
	ie_medicacao_paciente_w		varchar(1);
	ie_horario_especial_w		varchar(1);
	ie_medic_atrasado_w		varchar(1);
	ds_medic_nao_padrao_w		varchar(255);
	ds_observacao_w			varchar(2000);
	nr_dia_util_w2			bigint;
	ie_item_superior_w		varchar(1);
	nr_agrup_w				double precision;
	ie_apresenta_item_w		varchar(1);
	ie_interv_farm_w		varchar(1);
	ie_recem_nato_w			varchar(1);
	dt_plano_w				timestamp;
	ie_via_aplicacao_w		prescr_material.ie_via_aplicacao%type;
	qt_solucao_w			prescr_material.qt_solucao%type;
	
	
	c01 REFCURSOR;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ds_sep_bv_w	:= obter_separador_bv;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				hr_prim_horario,
				ie_composto,
				ie_plano_atual,
				ie_medicacao_paciente,
				dt_fim_horario,
				ie_horario_especial,
				ds_medic_nao_padrao,
				ds_observacao,
				nr_agrup,
				ie_item_superior,
				ie_prescr_farm,
				ie_recem_nato,
				ie_via_aplicacao,
				qt_solucao
		from	(	SELECT	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							CASE WHEN 	coalesce(c.dt_recusa::text, '') = '' THEN								SUBSTR(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep, c.dt_lib_horario),1,15)  ELSE substr(obter_status_recusa_pac(c.dt_fim_horario,c.dt_suspensao),1,15) END  ie_status_horario,
							c.cd_material,
							y.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							CASE WHEN coalesce(x.ds_dose_diferenciada::text, '') = '' THEN x.qt_dose  ELSE coalesce(c.qt_dose,x.qt_dose) END  qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao,
							'N' ie_diferenciado,
							CASE WHEN coalesce(obter_se_mostra_adic(7),'S')='N' THEN null  ELSE substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) END  ds_dil_obs,
							x.nr_dia_util,
							coalesce(CASE WHEN coalesce(x.dt_suspensao::text, '') = '' THEN  substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) END ,'X') ie_lib_pend_rep,
							c.dt_horario,
							CASE WHEN get_ie_apresenta_prim_hor='S' THEN coalesce(x.hr_prim_horario,'XP')  ELSE 'XP' END  hr_prim_horario,
							substr(coalesce(obter_se_medic_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),'N'),1,1) ie_composto,
							substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1) ie_plano_atual,
							coalesce(x.ie_medicacao_paciente,'N') ie_medicacao_paciente,
							c.dt_fim_horario,
							coalesce(c.ie_horario_especial,'N') ie_horario_especial,
							x.ds_medic_nao_padrao,
							CASE WHEN coalesce(obter_se_mostra_adic(7),'S')='N' THEN null  ELSE substr(x.ds_observacao,1,2000) END  ds_observacao,
							x.nr_agrupamento nr_agrup,
							x.ie_item_superior,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.ie_via_aplicacao,
							x.dt_suspensao dt_susp_item,
							x.qt_solucao
					from	material y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	y.cd_material = x.cd_material
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material	
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao	
					and		a.nr_atendimento = nr_atendimento_p
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p and dt_fim_w	
					and		x.ie_agrupador = 1
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 1
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_medicamento_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_medicamento_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'MED', 
																									wheb_usuario_pck.get_cd_estabelecimento, 
																									wheb_usuario_pck.get_cd_setor_atendimento, 
																									wheb_usuario_pck.get_cd_perfil, 
																									x.cd_material, 
																									null, 
																									null, 
																									null,
																									a.cd_setor_Atendimento,
																									null, 
																									a.nr_prescricao,
																									null) = 'S')))
					group by
							a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							c.dt_fim_horario,
							c.dt_suspensao,
							c.ie_dose_especial,
							c.nr_seq_processo,
							c.nr_seq_area_prep,
							c.cd_material,
							y.ds_material,
							CASE WHEN get_ie_apresenta_prim_hor='S' THEN coalesce(x.hr_prim_horario,'XP')  ELSE 'XP' END  ,
							coalesce(CASE WHEN coalesce(x.dt_suspensao::text, '') = '' THEN  substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) END ,'X'),
							x.ie_acm,
							x.ie_se_necessario,		
							x.cd_intervalo,
							CASE WHEN coalesce(x.ds_dose_diferenciada::text, '') = '' THEN x.qt_dose  ELSE coalesce(c.qt_dose,x.qt_dose) END ,
							x.nr_agrupamento,
							c.ie_controlado,
							x.ie_suspenso,
							x.ds_observacao,
							x.nr_dia_util,
							c.dt_lib_horario,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							obter_se_medic_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),
							substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1),
							c.dt_recusa,
							x.ie_medicacao_paciente,
							c.dt_fim_horario,
							coalesce(c.ie_horario_especial,'N'),
							x.ds_medic_nao_padrao,
							x.ds_observacao,
							x.nr_agrupamento,
							x.ie_item_superior,
							a.ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N'),
							x.ie_via_aplicacao,
							x.dt_suspensao,
							x.qt_solucao) alias61
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'M',
											nr_seq_material,			nr_prescricao,			'S') = 'S'
		group by
				nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				hr_prim_horario,
				ie_composto,
				ie_plano_atual,
				ie_medicacao_paciente,
				dt_fim_horario,
				ie_horario_especial,
				ds_medic_nao_padrao,
				ds_observacao,
				nr_agrup,
				ie_item_superior,
				ie_prescr_farm,
				ie_recem_nato,
				ie_via_aplicacao,
				qt_solucao
		order by
				dt_horario,
				dt_suspensao;	
	else
		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				hr_prim_horario,
				ie_composto,
				ie_plano_atual,
				ie_medicacao_paciente,
				dt_fim_horario,
				ie_horario_especial,
				ds_medic_nao_padrao,
				ds_observacao,
				nr_agrup,
				ie_item_superior,
				ie_prescr_farm,
				ie_recem_nato,
				ie_via_aplicacao,
				qt_solucao
		from	(	SELECT	a.nr_prescricao,
							c.nr_seq_material,
							c.nr_sequencia,
							CASE WHEN 	coalesce(c.dt_recusa::text, '') = '' THEN								SUBSTR(obter_status_hor_medic_lib(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep, c.dt_lib_horario),1,15)  ELSE substr(obter_status_recusa_pac(c.dt_fim_horario,c.dt_suspensao),1,15) END  ie_status_horario,
							c.cd_material,
							y.ds_material,
							obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
							x.cd_intervalo,
							CASE WHEN coalesce(x.ds_dose_diferenciada::text, '') = '' THEN x.qt_dose  ELSE coalesce(c.qt_dose,x.qt_dose) END  qt_dose,
							CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
							c.ie_controlado,
							coalesce(x.ie_suspenso,'N') ie_suspenso,
							c.dt_suspensao,
							'N' ie_diferenciado,
							CASE WHEN coalesce(obter_se_mostra_adic(7),'S')='N' THEN null  ELSE substr(adep_obter_inf_dil_obs(a.nr_prescricao,c.nr_seq_material),1,2000) END  ds_dil_obs,
							x.nr_dia_util,
							coalesce(CASE WHEN coalesce(x.dt_suspensao::text, '') = '' THEN  substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) END ,'X') ie_lib_pend_rep,
							c.dt_horario,
							CASE WHEN get_ie_apresenta_prim_hor='S' THEN coalesce(x.hr_prim_horario,'XP')  ELSE 'XP' END  hr_prim_horario,
							substr(coalesce(obter_se_medic_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),'N'),1,1) ie_composto,
							substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1) ie_plano_atual,
							coalesce(x.ie_medicacao_paciente,'N') ie_medicacao_paciente,
							c.dt_fim_horario,
							coalesce(c.ie_horario_especial,'N') ie_horario_especial,
							x.ds_medic_nao_padrao,
							CASE WHEN coalesce(obter_se_mostra_adic(7),'S')='N' THEN null  ELSE substr(x.ds_observacao,1,2000) END  ds_observacao,
							x.nr_agrupamento nr_agrup,
							x.ie_item_superior,
							coalesce(a.ie_prescr_farm, 'N') ie_prescr_farm,
							coalesce(a.ie_recem_nato, 'N') ie_recem_nato,
							x.ie_via_aplicacao,
							x.dt_suspensao dt_susp_item,
							x.qt_solucao
					from	material y,
							prescr_material x,
							prescr_mat_hor c,
							prescr_medica a
					where	y.cd_material = x.cd_material
					and		x.nr_prescricao = c.nr_prescricao
					and		x.nr_sequencia = c.nr_seq_material	
					and		x.nr_prescricao = a.nr_prescricao
					and		c.nr_prescricao = a.nr_prescricao	
					and		a.cd_pessoa_fisica = current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10)		
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_w')::bigint
					and		a.dt_validade_prescr	between dt_validade_limite_p and dt_fim_w	
					and		x.ie_agrupador = 1
					and		coalesce(x.nr_seq_kit::text, '') = ''
					and		plt_gerar_horarios_pck.obter_se_mostra_rep_inter(a.ie_motivo_prescricao) = 'S'
					and		coalesce(c.ie_situacao,'A') = 'A'
					and		c.ie_agrupador = 1
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_medicamento_w')::char(1) = 'S') or
							 ((current_setting('plt_gerar_horarios_pck.ie_regra_incl_medicamento_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'MED', 
																									wheb_usuario_pck.get_cd_estabelecimento, 
																									wheb_usuario_pck.get_cd_setor_atendimento, 
																									wheb_usuario_pck.get_cd_perfil, 
																									x.cd_material, 
																									null, 
																									null, 
																									null,
																									a.cd_setor_Atendimento,
																									null, 
																									a.nr_prescricao,
																									null) = 'S')))
					group by
						a.nr_prescricao,
						c.nr_seq_material,
						c.nr_sequencia,
						c.dt_fim_horario,
						c.dt_suspensao,
						c.ie_dose_especial,
						c.nr_seq_processo,
						c.nr_seq_area_prep,
						c.cd_material,
						y.ds_material,
						CASE WHEN get_ie_apresenta_prim_hor='S' THEN coalesce(x.hr_prim_horario,'XP')  ELSE 'XP' END  ,
						coalesce(CASE WHEN coalesce(x.dt_suspensao::text, '') = '' THEN  substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1) END ,'X'),
						x.ie_acm,
						x.ie_se_necessario,		
						x.cd_intervalo,
						CASE WHEN coalesce(x.ds_dose_diferenciada::text, '') = '' THEN x.qt_dose  ELSE coalesce(c.qt_dose,x.qt_dose) END ,
						x.nr_agrupamento,
						c.ie_controlado,
						x.ie_suspenso,
						x.ds_observacao,
						x.nr_dia_util,
						c.dt_lib_horario,
						a.dt_liberacao_medico,
						a.dt_liberacao,
						a.dt_liberacao_farmacia,
						c.dt_horario,
						obter_se_medic_composto(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento),
						substr(PLT_obter_se_plano_atual(a.nr_prescricao),1,1),
						c.dt_recusa,
						x.ie_medicacao_paciente,
						c.dt_fim_horario,
						coalesce(c.ie_horario_especial,'N'),
						x.ds_medic_nao_padrao,
						x.ds_observacao,
						x.ie_item_superior,
						a.ie_prescr_farm,
						coalesce(a.ie_recem_nato, 'N'),
						x.ie_via_aplicacao,
						x.dt_suspensao,
						x.qt_solucao) alias62
		where	plt_obter_se_item_periodo(	dt_validade_limite_p,		dt_fim_w,				get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,		ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_susp_item,		'M',
											nr_seq_material,			nr_prescricao,			'S') = 'S'
		group by
				nr_prescricao,
				nr_seq_material,
				nr_sequencia,
				ie_status_horario,
				cd_material,
				ds_material,
				ie_acm_sn,		
				cd_intervalo,
				qt_dose,
				nr_agrupamento,
				ie_controlado,
				ie_suspenso,
				dt_suspensao,
				ie_diferenciado,
				ds_dil_obs,
				nr_dia_util,
				ie_lib_pend_rep,
				dt_horario,
				hr_prim_horario,
				ie_composto,
				ie_plano_atual,
				ie_medicacao_paciente,
				dt_fim_horario,
				ie_horario_especial,
				ds_medic_nao_padrao,
				ds_observacao,
				nr_agrup,
				ie_item_superior,
				ie_prescr_farm,
				ie_recem_nato,
				ie_via_aplicacao,
				qt_solucao
		order by
				dt_horario,
				dt_suspensao;			
	end if;
	
	nr_dia_util_w2 := 0;

	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ds_material_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ie_controlado_w,
			ie_status_w,
			dt_suspensao_w,
			ie_diferenciado_w,
			ds_dil_obs_w,
			nr_dia_util_w,
			current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
			current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp,
			hr_prim_horario_w,
			ie_composto_w,
			ie_plano_atual_w,
			ie_medicacao_paciente_w,
			dt_fim_horario_w,
			ie_horario_especial_w,
			ds_medic_nao_padrao_w,
			ds_observacao_w,
			nr_agrup_w,
			ie_item_superior_w,
			ie_interv_farm_w,
			ie_recem_nato_w,
			ie_via_aplicacao_w,
			qt_solucao_w;

	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ie_apresenta_item_w := 'S';		
		
		if (ie_composto_w = 'S') and (ie_item_superior_w = 'N') and (nr_seq_material_w > 1) then
							
			select	coalesce(max('N'),'S')
			into STRICT	ie_apresenta_item_w
			from	prescr_material
			where	nr_prescricao	= nr_prescricao_w
			and		nr_sequencia	< nr_seq_material_w
			and		nr_agrupamento	= nr_agrup_w
			and		ie_agrupador	= 1
			and		(nr_seq_kit IS NOT NULL AND nr_seq_kit::text <> '')  LIMIT 1;
		end if;
		
		if (ie_apresenta_item_w = 'S') then
		
			nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );

			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			
				dt_plano_w := to_date(dt_plano_p,'dd/mm/yyyy');
			
				if (Obter_Se_Prim_Dia_Util_Prescr(nr_prescricao_w,dt_plano_w) = 'S') then
					nr_dia_util_ww	:=	nr_dia_util_w;
				else
					nr_dia_util_ww	:=	nr_dia_util_w + 1;
				end if;
					
				if (coalesce(current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),'X') <> 'M') then
					select	max(ie_pendente_liberacao)
					into STRICT	ie_lib_pend_rep_2w
					from	w_rep_t
					where	nm_usuario = nm_usuario_p
					and 	ie_tipo_item = 'M'
					and 	coalesce(nr_prescricao,coalesce(nr_prescricao_w,0)) = coalesce(nr_prescricao_w,0)
					and 	coalesce(nr_seq_item,coalesce(nr_seq_material_w,0)) = coalesce(nr_seq_material_w,0)
					and 	cd_item 	= cd_material_w
					and	ie_liberado 	= 'S'
					and	coalesce(ie_pendente_liberacao,'X') <> 'M'
					and 	coalesce(cd_intervalo,'0') = coalesce(cd_intervalo_w,'0')
					and 	coalesce(qt_item,0) 	= coalesce(qt_dose_w,0)
					and 	coalesce(ds_medic_nao_padrao,'XPTO') 	= coalesce(ds_medic_nao_padrao_w,'XPTO')
					and		coalesce(ie_composto,'N') = ie_composto_w
					and	coalesce(nr_dia_util,0) = coalesce(nr_dia_util_w,0);
								
					if (ie_lib_pend_rep_2w IS NOT NULL AND ie_lib_pend_rep_2w::text <> '') then
						PERFORM set_config('plt_gerar_horarios_pck.ie_lib_pend_rep_w', ie_lib_pend_rep_2w, false);
					end if;
				end if;
				
				ds_comando_update_w	:=	' update w_rep_t ' ||
								' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
								' ds_diluicao = :ds_dil_obs, ' ||
								' ds_hint = :ds_dil_obs ' ||
								' where nm_usuario = :nm_usuario ' ||
								' and ie_tipo_item = :ie_tipo ' ||
								' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
								' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
								' and cd_item = :cd_item ' ||
								' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
								' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
								' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
								' and nvl(hr_prim_horario,''XP'') = :hr_prim_horario ' ||
								' and nvl(ie_status_item,''N'') = :ie_status ' ||
								' and nvl(nr_agrupamento,0) = nvl(:nr_agrupamento,0) ' ||
								' and nvl(ie_composto,''N'') = :ie_composto ' ||
								' and nvl(ie_medicacao_paciente,''N'') = :ie_medic_paci' ||
								' and nvl(ds_medic_nao_padrao,''XPTO'') = nvl(:ds_medic_nao_padrao,''XPTO'')'||
								' and nvl(ds_observacao,''XPTO'') = nvl(:ds_observacao,''XPTO'')' ||
								' and nvl(nr_dia_util,0) = nvl(:nr_dia_util,0)' ||
								' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm' ||
								' and nvl(ie_via_aplicacao, ''N'') = :ie_via_aplicacao' ||
								' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ' ||
								' and nvl(qt_solucao,0) = :qt_solucao ';

				CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
											'ds_dil_obs=' || ds_dil_obs_w || ds_sep_bv_w || 
											'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
											'ie_tipo=M' || ds_sep_bv_w ||
											'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
											'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
											'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||																
											'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
											'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
											'hr_prim_horario=' || hr_prim_horario_w || ds_sep_bv_w ||
											'ie_status=' || to_char(ie_status_w) || ds_sep_bv_w ||
											'nr_agrupamento=' || to_char(nr_agrupamento_w) || ds_sep_bv_w ||
											'ie_composto=' || ie_composto_w || ds_sep_bv_w ||
											'ie_medic_paci=' || ie_medicacao_paciente_w || ds_sep_bv_w ||
											'ds_medic_nao_padrao=' || ds_medic_nao_padrao_w || ds_sep_bv_w ||
											'ds_observacao='||ds_observacao_w||ds_sep_bv_w||
											'nr_dia_util='||nr_dia_util_w||ds_sep_bv_w ||
											'ie_interv_farm=' || ie_interv_farm_w || ds_sep_bv_w ||
											'ie_via_aplicacao=' || ie_via_aplicacao_w || ds_sep_bv_w ||
											'ie_recem_nato=' || ie_recem_nato_w || ds_sep_bv_w ||
											'qt_solucao=' || coalesce(qt_solucao_w,0) || ds_sep_bv_w);
				
				nr_dia_util_w2	:= nr_dia_util_w;
				
				if (nr_dia_util_w <> nr_dia_util_ww) then
					select  coalesce(max(nr_dia_util),nr_dia_util_w2)
					into STRICT	nr_dia_util_w
					from	w_rep_t
					where 	nm_usuario = nm_usuario_p
					and 	ie_tipo_item = 'M'
					and 	coalesce(nr_prescricao,coalesce(nr_prescricao_w,0)) = coalesce(nr_prescricao_w,0)
					and 	coalesce(nr_seq_item,coalesce(nr_seq_material_w,0)) = coalesce(nr_seq_material_w,0) 			
					and 	cd_item = cd_material_w
					and 	coalesce(ie_pendente_liberacao,'X') = current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)
					and 	coalesce(cd_intervalo,0) = coalesce(cd_intervalo_w,0)
					and 	coalesce(qt_item,0) = coalesce(qt_dose_w,0) 
					and 	coalesce(hr_prim_horario,'XP') = hr_prim_horario_w
					and 	coalesce(ie_status_item,'N') = ie_status_w
					and 	coalesce(nr_agrupamento,0) = coalesce(nr_agrupamento_w,0) 
					and 	coalesce(ie_composto,'N') = ie_composto_w
					and 	coalesce(ie_medicacao_paciente,'N') = ie_medicacao_paciente_w
					and 	coalesce(ds_medic_nao_padrao,'XPTO') = coalesce(ds_medic_nao_padrao_w,'XPTO')
					and 	coalesce(ds_observacao,'XPTO') = coalesce(ds_observacao_w,'XPTO')
					and 	coalesce(nr_dia_util,0) = coalesce( nr_dia_util_ww ,0)
					and 	coalesce(ie_interv_farm,'N') = coalesce(ie_interv_farm_w, 'N')
					and 	coalesce(ie_item_rn,'N') = coalesce(ie_recem_nato_w, 'N')
					and 	coalesce(qt_solucao,0) = coalesce(qt_solucao_w,0);
				end if;							
											
				if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') and
					((dt_fim_horario_w IS NOT NULL AND dt_fim_horario_w::text <> '') or (ie_horario_especial_w = 'N')) then
					ie_medic_atrasado_w := 'N';
					
					if (ie_status_horario_w not in ('A', 'S')) and (coalesce(get_qt_minutos_atraso,0) > 0) then
						select 	obter_se_medic_atraso_adep(nr_seq_horario_w, ie_status_horario_w, get_qt_minutos_atraso)
						into STRICT	ie_medic_atrasado_w
						;
						
					end if;
					
					ds_comando_update_w	:=	' update w_rep_t ' ||
									' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
									' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
									' ie_diferenciado = :ie_diferenciado, ' ||
									' ds_diluicao = :ds_dil_obs, ' ||
									' ds_hint = :ds_dil_obs, ' ||							
									' ie_medic_controlado = :ie_controlado, ' ||
									' nr_dia_util = :nr_dia_util_ww, ' ||
									' ie_plano_atual = decode(ie_plano_atual,''S'',''S'',:ie_plano_atual), ' ||
									' ie_atrasado = :ie_atrasado ' ||
									' where nm_usuario = :nm_usuario ' ||
									' and ie_tipo_item = :ie_tipo ' ||
									' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
									' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
									' and cd_item = :cd_item ' ||
									' and nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao ' ||
									' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
									' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
									' and nvl(hr_prim_horario,''XP'') = :hr_prim_horario ' ||
									' and nvl(ie_status_item,''N'') = :ie_status ' ||
									' and nvl(nr_agrupamento,0) = nvl(:nr_agrupamento,0) ' ||
									' and nvl(ie_composto,''N'') = :ie_composto ' ||
									' and nvl(ie_medicacao_paciente,''N'') = :ie_medic_paci ' ||
									' and nvl(ds_medic_nao_padrao,''XPTO'') = nvl(:ds_medic_nao_padrao,''XPTO'') '||
									' and nvl(ds_observacao,''XPTO'') = nvl(:ds_observacao,''XPTO'')' ||
									' and nvl(nr_dia_util,0) = nvl(:nr_dia_util,0) ' ||
									' and nvl(ie_interv_farm, ''N'') = :ie_interv_farm' ||
									' and nvl(ie_via_aplicacao, ''N'') = :ie_via_aplicacao' ||
									' and nvl(ie_item_rn, ''N'') = :ie_recem_nato ' ||
									' and nvl(qt_solucao,0) = :qt_solucao ';
								
					CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
												'ie_diferenciado=' || ie_diferenciado_w || ds_sep_bv_w || 
												'ds_dil_obs=' || ds_dil_obs_w || ds_sep_bv_w || 
												'ie_controlado=' || ie_controlado_w || ds_sep_bv_w || 
												'nr_dia_util=' || to_char(nr_dia_util_w) || ds_sep_bv_w || 
												'nr_dia_util_ww=' || to_char(nr_dia_util_ww) || ds_sep_bv_w || 
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=M' || ds_sep_bv_w ||
												'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
												'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
												'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||																
												'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
												'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
												'hr_prim_horario=' || hr_prim_horario_w || ds_sep_bv_w ||
												'ie_status=' || to_char(ie_status_w) || ds_sep_bv_w ||
												'nr_agrupamento=' || to_char(nr_agrupamento_w) || ds_sep_bv_w ||
												'ie_composto=' || ie_composto_w || ds_sep_bv_w ||
												'ie_plano_atual=' || ie_plano_atual_w || ds_sep_bv_w ||
												'ie_medic_paci=' || ie_medicacao_paciente_w || ds_sep_bv_w ||
												'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w ||
												'ds_medic_nao_padrao=' || ds_medic_nao_padrao_w || ds_sep_bv_w ||
												'ds_observacao='||ds_observacao_w||ds_sep_bv_w ||
												'ie_interv_farm=' || ie_interv_farm_w || ds_sep_bv_w||
												'ie_via_aplicacao=' || ie_via_aplicacao_w || ds_sep_bv_w||
												'ie_recem_nato=' || ie_recem_nato_w || ds_sep_bv_w || 
												'qt_solucao=' || coalesce(qt_solucao_w,0) || ds_sep_bv_w );
												
				end if;
			end if;
		end if;
		end;
	end loop;
	close c01;

	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p, current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'M', 'N',null);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_medic ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
