SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_npt_adul ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

			
	ds_sep_bv_w			varchar(50);				
	nr_prescricao_w		bigint;
	nr_seq_solucao_w	bigint;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	ds_comando_update_w	varchar(4000);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_w			timestamp;
	
	
	c01 REFCURSOR;

	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ds_sep_bv_w	:= obter_separador_bv;
	
	if (current_setting('plt_gerar_horarios_pck.ie_regra_incl_npt_adulta_w')::char(1) not in ('S','R')) then
		return;
	end if;
	
	-- Restricoes padroes do WHERE

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		a.nr_atendimento = ' || nr_atendimento_p || ' ', false);
	else
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		a.cd_pessoa_fisica = ''' || current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10) || ''' ', false);
	end if;	
	
	if (current_setting('plt_gerar_horarios_pck.ie_regra_incl_npt_adulta_w')::char(1) = 'R') then
		PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000) || ' and		(adep_obter_regra_inclusao(	''NAN'',
																							''' || wheb_usuario_pck.get_cd_estabelecimento || ''',
																							''' || wheb_usuario_pck.get_cd_setor_atendimento || ''',
																							''' || wheb_usuario_pck.get_cd_perfil || ''', 
																							null, 
																							null, 
																							null, 
																							null, 
																							a.cd_Setor_atendimento,
																							null,
																							null,
																							null) = ''S'') ', false); -- nr_seq_exame_p. Passei o nr_prescricao_p null porque foi criado o parametro como default null, e nao havia sido passado aqui
	end if;
	
	-- Criacao da consulta SELECT

	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_w', ' select	nr_prescricao, ' ||
	' 			nr_sequencia, ' ||
	' 			ie_status, ' ||
	' 			dt_horario ' ||
	' from	(' ||
			'	select	a.nr_prescricao, ' ||
			'			x.nr_sequencia nr_sequencia, ' ||
			'			decode(x.ie_suspenso, ''S'', ''S'', decode(nvl(x.ie_status,''W''),''P'',''W'',''INT'',''T'',''T'',''A'',nvl(substr(obter_status_solucao_prescr(6,a.nr_prescricao,x.nr_sequencia),1,3),''W''))) ie_status, ' ||
			'			x.dt_status dt_horario ' ||
			'	from	nut_pac x, ' ||
			'			prescr_medica a ' ||
			'	where	x.nr_prescricao = a.nr_prescricao ' ||

			'	and		not exists(	select 1 from nut_paciente_hor z where z.nr_seq_nut_protocolo = x.nr_sequencia ) ' ||
			'	and		nvl(x.ie_acm,''N'') = ''N'' ' ||
			'	and		a.dt_validade_prescr between ' ||
							' to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss') || ''', ''dd/mm/yyyy hh24:mi:ss'') ' ||
			'	and		x.dt_status between ' ||
							' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss') || ''', ''dd/mm/yyyy hh24:mi:ss'') ' ||
			'	and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = ''S'' ' ||
			'	and		plt_obter_se_item_periodo(	  to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || get_ie_plano_por_setor || ''', ' ||
													' ''' || dt_plano_p || ''', ' ||
													' x.dt_status, ' ||
													' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_prescr_usuario_p || ''', '||
													' ''' || nm_usuario_p || ''', '||
													' ''' || ie_estendidos_p || ''', '||
													' to_date(''' || to_char(dt_referencia_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_edicao_p || ''', '||
													' ''' || ie_exibir_hor_suspensos_p || ''', '||
													' x.dt_suspensao, ' ||
													' ''NAN'', '||
													' x.nr_sequencia, ' ||
													' a.nr_prescricao, ' ||
													' ''O'' ) = ''S'' ' ||
			current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000)	||
			'	group by	a.nr_prescricao, ' ||
			'				x.nr_sequencia, ' ||
			'				decode(x.ie_suspenso, ''S'', ''S'', decode(nvl(x.ie_status,''W''),''P'',''W'',''INT'',''T'',''T'',''A'',nvl(substr(obter_status_solucao_prescr(6,a.nr_prescricao,x.nr_sequencia),1,3),''W''))), ' ||
			'				x.dt_status ' ||
			' union all ' ||
			'	select	a.nr_prescricao, ' ||
			'			x.nr_sequencia nr_sequencia, ' ||
			'			decode(x.ie_suspenso, ''S'', ''S'', decode(nvl(b.ie_status,''N''),''INT'',''T'',''T'',''A'',nvl(b.ie_status,''N''))) ie_status, ' ||
			'			b.dt_horario dt_horario ' ||
			'	from	nut_paciente_hor b, ' ||
			'			nut_pac x, ' ||
			'			prescr_medica a ' ||
			'	where	x.nr_prescricao = a.nr_prescricao ' ||
			'	and		b.nr_seq_nut_protocolo	= x.nr_sequencia ' ||
			'	and		(nvl(b.ie_horario_especial,''N'') = ''N'') ' ||
			'	and		a.dt_validade_prescr between ' ||
							' to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss') || ''', ''dd/mm/yyyy hh24:mi:ss'') ' ||
			'	and		b.dt_horario between ' ||
							' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss'') and ' ||
							' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss') || ''', ''dd/mm/yyyy hh24:mi:ss'') ' ||
			'	and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = ''S'' ' ||
			'	and		plt_obter_se_item_periodo(	to_date(''' || to_char(dt_validade_limite_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(dt_fim_w,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || get_ie_plano_por_setor || ''', '||
													' ''' || dt_plano_p || ''', ' ||
													' b.dt_horario, ' ||
													' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' to_date(''' || to_char(current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_prescr_usuario_p || ''', '||
													' ''' || nm_usuario_p || ''', '||
													' ''' || ie_estendidos_p || ''', ' ||
													' to_date(''' || to_char(dt_referencia_p,'dd/mm/yyyy hh24:mi:ss')  || ''', ''dd/mm/yyyy hh24:mi:ss''), ' ||
													' ''' || ie_edicao_p || ''', ' ||
													' ''' || ie_exibir_hor_suspensos_p || ''', ' || 
													' x.dt_suspensao, ' || 
													' ''NAN'', ' || 
													' x.nr_sequencia, ' || 
													' a.nr_prescricao, ' || 
													' ''O'' ) = ''S'' ' ||
			current_setting('plt_gerar_horarios_pck.sql_c01_restricao_w')::varchar(10000)	||
			'	group by	a.nr_prescricao, ' ||
			'				x.nr_sequencia, ' ||
			'				decode(x.ie_suspenso, ''S'', ''S'', decode(nvl(b.ie_status,''N''),''INT'',''T'',''T'',''A'',nvl(b.ie_status,''N''))), ' ||
			'				b.dt_horario ' ||
	'		) ' ||
	' group by	nr_prescricao, ' ||
	'			nr_sequencia, ' ||
	'			ie_status, ' ||
	'			dt_horario ', false);

	open c01 for EXECUTE current_setting('plt_gerar_horarios_pck.sql_c01_w')::varchar(20000);	
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_solucao_w,
			ie_status_horario_w,
			current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_rep_t ' ||
									' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
									' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
									' where nm_usuario = :nm_usuario ' ||
									' and ie_tipo_item = :ie_tipo ' ||
									' and nvl(nr_prescricao, :nr_prescricao) = :nr_prescricao ' ||
									' and nvl(nr_seq_item, :nr_seq_item) = :nr_seq_item ';

						
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(null) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || coalesce(to_char(nr_prescricao_w),'0')  || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=NAN' || ds_sep_bv_w ||
										'nr_seq_item=' || coalesce(to_char(nr_seq_solucao_w),'0') || ds_sep_bv_w );

		end if;
		end;
	end loop;
	
	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_w', null, false);
	PERFORM set_config('plt_gerar_horarios_pck.sql_c01_restricao_w', null, false);
	
	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p, current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'NPA', 'N',null);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_npt_adul ( nm_usuario_p text, nr_atendimento_p bigint, dt_validade_limite_p timestamp, ie_prescr_usuario_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
