CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_suspensos_p text) AS $body$
DECLARE

				
	ds_sep_bv_w		varchar(50);				
	nr_prescricao_w		bigint;
	nr_seq_item_ordem_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	nr_seq_ordem_w		bigint;
	ds_ordem_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	ds_prescricao_w		varchar(255);
	dt_suspensao_w		timestamp;
	ds_comando_update_w	varchar(4000);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp	timestamp;
	nr_horario_w	integer;
	dt_fim_w	timestamp;	
	
	c01 CURSOR FOR
	SELECT	a.nr_prescricao,
			b.nr_seq_solucao nr_seq_item_ordem,
			c.nr_sequencia,
			substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
			b.nr_sequencia nr_seq_ordem,
			x.ds_ordem,
			'N' ie_acm_sn,
			obter_desc_solucao_adep(a.nr_prescricao,b.nr_seq_solucao) ds_prescricao,
			c.dt_suspensao dt_suspensao,
			c.dt_horario
	from	ordem_medica x,
			prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a
	where	x.nr_sequencia = b.nr_seq_ordem
	and		c.nr_seq_ordem = b.nr_sequencia
	and		b.nr_prescricao = a.nr_prescricao
	and		a.nr_atendimento = nr_atendimento_p
	and		a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and		(b.nr_seq_solucao IS NOT NULL AND b.nr_seq_solucao::text <> '')
	and		((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and		c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and		coalesce(c.ie_horario_especial,'N') = 'N'
	group by
		c.dt_horario,
		a.nr_prescricao,
		b.nr_seq_solucao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		b.nr_sequencia,
		x.ds_ordem,
		'N'
	
union all

	SELECT	a.nr_prescricao,
		b.nr_seq_material nr_seq_item_ordem,
		c.nr_sequencia,
		substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
		b.nr_sequencia nr_seq_ordem,
		x.ds_ordem,
		'N' ie_acm_sn,
		z.ds_material ds_prescricao,
		c.dt_suspensao dt_suspensao,
		c.dt_horario
	from	material z,
		prescr_material y,
		ordem_medica x,
		prescr_ordem_hor c,
		prescr_medica_ordem b,
		prescr_medica a
	where	z.cd_material = y.cd_material
	and	y.nr_prescricao = a.nr_prescricao
	and	y.nr_prescricao = b.nr_prescricao
	and	y.nr_sequencia = b.nr_seq_material
	and	x.nr_sequencia = b.nr_seq_ordem
	and	c.nr_seq_ordem = b.nr_sequencia
	and	b.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and coalesce(c.ie_horario_especial,'N') = 'N'
	group by
		c.dt_horario,
		a.nr_prescricao,
		b.nr_seq_material,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		b.nr_sequencia,
		x.ds_ordem,
		'N',
		z.ds_material
	order by
		dt_horario,
		dt_suspensao;
		
	
BEGIN
	if (get_ie_apresenta_ordem_med = 'S') then
		ds_sep_bv_w := obter_separador_bv;
		dt_fim_w	:= dt_validade_limite_p + 5;
		open c01;
		loop
		fetch c01 into	nr_prescricao_w,
				nr_seq_item_ordem_w,
				nr_seq_horario_w,
				ie_status_horario_w,
				nr_seq_ordem_w,
				ds_ordem_w,
				ie_acm_sn_w,
				ds_prescricao_w,
				dt_suspensao_w,
				current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
				ds_comando_update_w	:=	' update w_rep_t ' ||
								' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
								' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
								' where nm_usuario = :nm_usuario ' ||
								' and ie_tipo_item = :ie_tipo ' ||
								' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
								' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
								' and cd_item = :cd_item ' ||
								' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';
							
				CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
											'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
											'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
											'ie_tipo=B' || ds_sep_bv_w ||
											'nr_seq_item=' || to_char(nr_seq_item_ordem_w) || ds_sep_bv_w ||
											'cd_item=' || to_char(nr_seq_horario_w) || ds_sep_bv_w ||
											'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w );
				commit;
			end if;
			end;
		end loop;
		close c01;
	end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_suspensos_p text) FROM PUBLIC;
