CREATE OR REPLACE PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_solucao ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_formato_solucoes_p text, ie_prescr_usuario_p text, ie_sol_continuas_p text, ie_sol_vigentes_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) AS $body$
DECLARE

					
	ds_sep_bv_w					varchar(50);				
	nr_prescricao_w				bigint;
	nr_seq_solucao_w			integer;
	nr_seq_horario_w			bigint;
	ie_status_horario_w			varchar(15);
	ds_comando_update_w			varchar(4000);
	dt_etapa_prev_w				timestamp;
	current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)			varchar(1);
	current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp				timestamp;
	nr_horario_w				integer;
	dt_fim_w					timestamp;
	hr_prim_horario_w			varchar(5);
	nr_prescr_continua_w		bigint;
	nr_seq_continua_w			bigint;	
	nr_etapa_sol_w				smallint;
	
	
	c01 REFCURSOR;
	c02 REFCURSOR;
		
	
BEGIN
	dt_fim_w	:= dt_validade_limite_p + 5;
	ds_sep_bv_w	:= obter_separador_bv;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

		open	c01 for
		SELECT	nr_prescricao,
				nr_seq_solucao,
				ie_status_horario,
				ie_lib_pend_rep,
				dt_status,
				hr_prim_horario,
				nr_prescr_continua,
				nr_seq_continua
		from	(	SELECT	a.nr_prescricao,
							x.nr_seq_solucao,
							CASE WHEN x.ie_status='P' THEN  'W'  ELSE substr(obter_status_hor_sol_processo(a.nr_prescricao,x.nr_seq_solucao,1),1,15) END  ie_status_horario,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							x.dt_status,
							x.hr_prim_horario,
							plt_obter_se_sol_continua(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,'P') nr_prescr_continua,
							plt_obter_se_sol_continua(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,'S') nr_seq_continua,
							x.dt_suspensao
					from	prescr_solucao x,
							prescr_medica a
					where	x.nr_prescricao = a.nr_prescricao
					and		a.nr_atendimento = nr_atendimento_p		
					and		a.nr_prescricao	between current_setting('plt_gerar_horarios_pck.nr_prescr_inicial_sol_w')::bigint and current_setting('plt_gerar_horarios_pck.nr_prescr_final_sol_w')::bigint
					and		coalesce(x.nr_seq_dialise::text, '') = ''
					and		PLT_Gerar_Horarios_PCK.OBTER_SE_MOSTRA_REP_INTER(a.ie_motivo_prescricao) = 'S'
					and		x.ie_status in ('P','N')
					and		((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'S') or
							((current_setting('plt_gerar_horarios_pck.ie_regra_incl_solucao_w')::char(1) = 'R') and (adep_obter_regra_inclusao(	'SOL',
																								wheb_usuario_pck.get_cd_estabelecimento, 
																								wheb_usuario_pck.get_cd_setor_atendimento, 
