																								null,
																								a.cd_setor_Atendimento,
																								null,
																								null,
																								null) = 'S')))
					group by
							a.nr_prescricao,
							x.nr_seq_solucao,
							x.ie_status,
							a.dt_liberacao_medico,
							a.dt_liberacao,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X'),
							a.dt_liberacao_farmacia,
							x.dt_status,
							x.hr_prim_horario,
							x.ds_solucao,
							x.dt_suspensao) alias28
		where	plt_obter_se_item_periodo(	null,						null,					get_ie_plano_por_setor,		dt_plano_p,		dt_status,
											current_setting('plt_gerar_horarios_pck.dt_horario_inicio_sinc_w')::timestamp,	current_setting('plt_gerar_horarios_pck.dt_horario_fim_sinc_w')::timestamp,	ie_prescr_usuario_p,		nm_usuario_p,	ie_estendidos_p,
											dt_referencia_p,			ie_edicao_p,			ie_exibir_hor_suspensos_p,	dt_suspensao,	'SOL',
											nr_seq_solucao,				nr_prescricao,			'S') = 'S'
		group by
				nr_prescricao,
				nr_seq_solucao,
				ie_status_horario,
				ie_lib_pend_rep,
				dt_status,
				hr_prim_horario,
				nr_prescr_continua,
				nr_seq_continua
		order by 
				dt_status;
			
		open	c02 for
		SELECT	nr_prescricao,
