					and		c.ie_agrupador = 4
					and		((coalesce(c.ie_horario_especial,'N') = 'N') or /*(c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '') or*/ (x.ie_etapa_especial = 'S'))	
					and		adep_obter_se_gerar_sol_hor(c.dt_horario, c.dt_inicio_horario, c.dt_fim_horario, c.dt_suspensao, dt_inicial_horarios_p, dt_final_horarios_p) = 'S'
					and		((ie_sol_vigentes_p	= 'N') or (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S') or (exists (	select	1 
										from	prescr_mat_hor b
