							a.dt_liberacao_medico,
							a.dt_liberacao,
							a.dt_liberacao_farmacia,
							c.dt_horario,
							c.nr_etapa_sol,
							x.dt_suspensao) alias37
		where	plt_obter_se_item_periodo(	null,						null,					get_ie_plano_por_setor,		dt_plano_p,			dt_horario,
