				nr_seq_solucao,
				ie_status_horario,
				dt_prev_fim_horario,
				ie_lib_pend_rep,
				dt_horario,
				nr_etapa_sol
		from	(	SELECT	a.nr_prescricao,
							x.nr_seq_solucao,
							substr(plt_obter_status_hor_sol_lib(c.dt_inicio_horario,c.dt_fim_horario,c.dt_suspensao,c.dt_interrupcao, c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,c.dt_lib_horario),1,15) ie_status_horario,
							c.dt_prev_fim_horario,
							coalesce(substr(plt_obter_lib_pend_prescr(a.dt_liberacao_medico,a.dt_liberacao,CASE WHEN a.ie_lib_farm='S' THEN a.dt_liberacao_farmacia  ELSE clock_timestamp() END ),1,1),'X') ie_lib_pend_rep,
							c.dt_horario,
