		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );
			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then			
				ds_comando_update_w	:=	' update w_rep_t ' ||
								' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
								' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
								' where nm_usuario = :nm_usuario ' ||
								' and ie_tipo_item = :ie_tipo ' ||
								' and (((:ie_solucoes_continuas_p = ''S'') and ' ||
								'	(nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) and  ' ||
								'       (nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) and	' ||
								'	(nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao)) or ' ||
								'      ((:ie_solucoes_continuas_p = ''N'') and ' ||
								'	(nvl(ie_acm_sn,''N'')  <> ''S'') and ' ||
								'	(((nvl(hr_prim_horario,nvl(:hr_prim_horario,''X'')) = nvl(:hr_prim_horario,''X'')) and '||
								'	  (nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao)) or ' ||
								'	 ((:nr_prescr_continua = nr_prescricao) and (:nr_seq_continua = nr_seq_item))) and ' ||
								'       (obter_se_valor_contido(nr_prescricao, :nr_prescricoes) = ''S'')))';
							
				CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
											'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
											'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
											'ie_tipo=SOL' || ds_sep_bv_w ||
											'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||
											'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
											'ie_solucoes_continuas_p=' || ie_sol_continuas_p || ds_sep_bv_w ||
											'hr_prim_horario=' || hr_prim_horario_w || ds_sep_bv_w ||
											'nr_prescr_continua=' || to_char(nr_prescr_continua_w) || ds_sep_bv_w ||
											'nr_seq_continua=' || to_char(nr_seq_continua_w) || ds_sep_bv_w ||
											'nr_prescricoes=' || current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000) );											
			end if;
			end;
		end loop;
		close c01;
		end;
	elsif (ie_formato_solucoes_p = 'H') then
		begin
		loop
		fetch c02 into	nr_prescricao_w,
				nr_seq_solucao_w,
				ie_status_horario_w,
				dt_etapa_prev_w,
				current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp,
				nr_etapa_sol_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			nr_horario_w := plt_gerar_horarios_pck.get_posicao_horario( current_setting('plt_gerar_horarios_pck.dt_horario_w')::timestamp );			

			if (coalesce(dt_etapa_prev_w::text, '') = '') then
				if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then	
					ds_comando_update_w	:=	' update w_rep_t ' ||
									' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
									' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
									' dt_prev_term =  null ' ||
									' where nm_usuario = :nm_usuario ' ||
									' and ie_tipo_item = :ie_tipo ' ||
									' and (((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) and  ' ||
									'       (nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) and	' ||
									'	(nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao)) or ' ||
									'      ((:ie_solucoes_continuas_p = ''N'') and ' ||
									'	(nvl(ie_acm_sn,''N'')  <> ''S'') and ' ||
									'	(nvl(nr_prescricao,nvl(:nr_prescricao,0)) <> nvl(:nr_prescricao,0)) and' ||
--									'	(obter_se_sol_continua(nr_prescricao,nr_seq_item,ds_item) = ''S'') and ' || 

									'       (obter_se_valor_contido(nr_prescricao, :nr_prescricoes) = ''S'')))';

					CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_etapa_sol_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'ie_solucoes_continuas_p=' || ie_sol_continuas_p || ds_sep_bv_w ||
												'nr_prescricoes=' || current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000) );
				
				end if;
			else
				if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then			
					ds_comando_update_w	:=	' update w_rep_t ' ||
									' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
									' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
									' dt_prev_term =  :dt_prev_term ' ||
									' where nm_usuario = :nm_usuario ' ||
									' and ie_tipo_item = :ie_tipo ' ||
									' and (((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) and  ' ||
									'       (nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) and	' ||
									'	(nvl(ie_pendente_liberacao,''X'') = :ie_pendente_liberacao)) or ' ||
									'      ((:ie_solucoes_continuas_p = ''N'') and ' ||
									'	(nvl(ie_acm_sn,''N'')  <> ''S'') and ' ||
									'	(nvl(nr_prescricao,nvl(:nr_prescricao,0)) <> nvl(:nr_prescricao,0)) and' ||
--									'	(obter_se_sol_continua(nr_prescricao,nr_seq_item,ds_item) = ''S'') and ' || 

									'       (obter_se_valor_contido(nr_prescricao, :nr_prescricoes) = ''S'')))';

					CALL exec_sql_dinamico_bv('PLT', ds_comando_update_w,	'vl_hora=S' || to_char(nr_etapa_sol_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
												'dt_prev_term=' || to_char(dt_etapa_prev_w, 'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w ||												
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ie_pendente_liberacao=' || current_setting('plt_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15) || ds_sep_bv_w ||
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'ie_solucoes_continuas_p=' || ie_sol_continuas_p || ds_sep_bv_w ||
												'nr_prescricoes=' || current_setting('plt_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000) );
				end if;
			end if;
				
			end;
		end loop;
		close c02;	
		end;
	end if;
	
	CALL Atualizar_plt_controle(nm_usuario_p, nr_atendimento_p, current_setting('plt_gerar_horarios_pck.cd_pessoa_fisica_w')::varchar(10), 'SOL', 'N',null);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE plt_gerar_horarios_pck.plt_sincronizar_solucao ( nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_formato_solucoes_p text, ie_prescr_usuario_p text, ie_sol_continuas_p text, ie_sol_vigentes_p text, ie_edicao_p text, ie_estendidos_p bigint, dt_referencia_p timestamp, ie_exibir_hor_suspensos_p text, dt_plano_p text) FROM PUBLIC;
