CREATE OR REPLACE FUNCTION prescricao_paciente_pck_md.calular_total_dispensar_md (qt_unitaria_p bigint, qt_minimo_multiplo_solic_p bigint) RETURNS bigint AS $body$
DECLARE

    qt_total_dispensar_w double precision;

BEGIN
    qt_total_dispensar_w := dividir_md(qt_unitaria_p, qt_minimo_multiplo_solic_p) * coalesce(qt_minimo_multiplo_solic_p, 0);
    if (qt_total_dispensar_w < qt_unitaria_p) then
      qt_total_dispensar_w := coalesce(qt_total_dispensar_w, 0) + coalesce(qt_minimo_multiplo_solic_p, 0);
    end if;
    return qt_total_dispensar_w;
  end;


