CREATE OR REPLACE FUNCTION prescription_json_pck.get_lis_message ( nr_prescricao_p bigint , dt_horario_p timestamp default null, nr_seq_prescr_p bigint default null) RETURNS PHILIPS_JSON AS $body$
DECLARE

	nr_prescricao_w		prescr_medica.nr_prescricao%type;
	json_return_w		philips_json;
	json_prescr_w		philips_json;
	json_item_w		philips_json;
	json_item_list_w   	philips_json_list;
	
BEGIN
	
	
	
	json_item_list_w	:= prescription_json_pck.get_lis_exams(nr_prescricao_p,dt_horario_p,nr_seq_prescr_p);
	
	
	if (json_item_list_w.count > 0) then
		json_return_w	:= philips_json();
		json_return_w		:= prescription_json_pck.get_default_message(nr_prescricao_p);
		json_prescr_w		:= prescription_json_pck.get_prescription(nr_prescricao_p);
		json_prescr_w.put('examsLisList',json_item_list_w.to_json_value());
		json_return_w.put('prescription',json_prescr_w.to_json_value());
	end if;
	
	return json_return_w;
	
	END;

$body$
