SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION prescription_json_pck.get_ris_message ( nr_seq_proc_hor_p bigint) RETURNS PHILIPS_JSON AS $body$
DECLARE

	
	nr_prescricao_w		prescr_medica.nr_prescricao%type;
	json_return_w		philips_json;
	json_prescr_w		philips_json;
	json_item_w		philips_json;
	json_item_list_w   	philips_json_list;
	json_surgery_w		philips_json;
	
BEGIN
	

	json_item_list_w	:= prescription_json_pck.get_ris_exams(nr_seq_proc_hor_p);
	
	if (json_item_list_w.count > 0) then
	
		select	max(nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_proc_hor
		where	nr_sequencia	= nr_seq_proc_hor_p;
	
		if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
			json_return_w		:= philips_json();
			json_return_w		:= prescription_json_pck.get_default_message(nr_prescricao_w);
			json_prescr_w		:= prescription_json_pck.get_prescription(nr_prescricao_w);
			json_surgery_w		:= prescription_json_pck.get_surgery(nr_prescricao_w);
			json_prescr_w.put('examsRisList',json_item_list_w.to_json_value());
			json_return_w.put('prescription',json_prescr_w.to_json_value());
			json_return_w.put('surgery',json_surgery_w.to_json_value());
		end if;
	end if;
	
	return json_return_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
