SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION prescription_json_pck.get_surgery ( nr_prescricao_p bigint ) RETURNS PHILIPS_JSON AS $body$
DECLARE

	nr_cirurgia_w		cirurgia.nr_cirurgia%type;
	json_surgery_w		philips_json;
	
	C01 CURSOR FOR
		SELECT		a.cd_medico_anestesista anesthetist_doctor_id,
				get_provider_details(a.cd_medico_anestesista, a.cd_estabelecimento, a.cd_setor_atendimento) anesthetist_provider_num,
				obter_dados_pf(a.cd_medico_anestesista,'PNG') anesthetist_Given_Name,
				obter_dados_pf(a.cd_medico_anestesista,'PNL') anesthetist_Last_Name,
				obter_dados_pf(a.cd_medico_anestesista,'PNM') anesthetist_Middle_Name
		from	cirurgia a
		where	nr_cirurgia = nr_cirurgia_w;
	
	
BEGIN
	
	select	max(nr_cirurgia)
	into STRICT	nr_cirurgia_w
	from	cirurgia
	where	nr_prescricao = nr_prescricao_p;
	
	json_surgery_w	:= philips_json();
	
	
	for r_c01 in c01 loop
		begin
		json_surgery_w := prescription_json_pck.add_json_value(json_surgery_w, 'anesthetistDoctorId', r_c01.anesthetist_doctor_id);
		json_surgery_w := prescription_json_pck.add_json_value(json_surgery_w, 'anesthetistProviderNum', r_c01.anesthetist_provider_num);
		json_surgery_w := prescription_json_pck.add_json_value(json_surgery_w, 'anesthetistGivenName', r_c01.anesthetist_Given_Name);
		json_surgery_w := prescription_json_pck.add_json_value(json_surgery_w, 'anesthetistLastName', r_c01.anesthetist_Last_Name);
		json_surgery_w := prescription_json_pck.add_json_value(json_surgery_w, 'anesthetistMiddleName', r_c01.anesthetist_Middle_Name);
		end;
	end loop;
	
	return json_surgery_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
