CREATE OR REPLACE FUNCTION product_mgmt_pck.is_product_installed (p_product_name text) RETURNS varchar AS $body$
DECLARE

    v_ret varchar(1);

BEGIN
    begin
      select is_product_installed
        into STRICT v_ret
        from product
       where product_name = p_product_name;
