CREATE OR REPLACE FUNCTION product_mgmt_pck.is_version_active (p_product_name text, p_product_version text) RETURNS varchar AS $body$
DECLARE

    v_ret varchar(1);

BEGIN
    begin
      select is_version_active
        into STRICT v_ret
        from product_version
       where product_name = p_product_name
         and product_version = p_product_version;
    exception
      when no_data_found then
        v_ret := 'N';
    end;
    return v_ret;
  end;

