SET client_encoding TO 'UTF8';

	
	--VC>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Ordem Compra <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar 1 se a ordem de compra passada por parametro possui valor no projeto recurso passado por parametro.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nr_ordem_compra_p = Numero da ordem de compra.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_ie_comprometido_ordem ( nr_seq_proj_rec_p bigint, nr_ordem_compra_p bigint) RETURNS bigint AS $body$
DECLARE

	
	
	qt_item_pend_oc_w		ordem_compra_item_entrega.qt_prevista_entrega%type;
	qt_total_item_pend_oc_w		ordem_compra_item_entrega.qt_prevista_entrega%type;
	vl_unitario_material_w		ordem_compra_item.vl_unitario_material%type;
	nr_item_oci_w			ordem_compra_item.nr_item_oci%type;	
	
	c01 CURSOR FOR	
		SELECT	b.vl_unitario_material,
			b.nr_item_oci
		from 	ordem_compra a,
			ordem_compra_item b
		where	a.nr_ordem_compra = b.nr_ordem_compra
		and	b.nr_seq_proj_rec = nr_seq_proj_rec_p
		and     a.nr_ordem_compra = nr_ordem_compra_p
		and     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and     coalesce(a.nr_seq_motivo_cancel::text, '') = ''
		and	coalesce(b.dt_reprovacao::text, '') = ''
		order by 1;
	
	
BEGIN
	
	qt_item_pend_oc_w 		:= 0;
	qt_total_item_pend_oc_w 	:= 0;
	
	open c01;
	loop
	fetch c01 into
		vl_unitario_material_w,
		nr_item_oci_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		select	coalesce(sum(qt_prevista_entrega),0) - coalesce(sum(qt_real_entrega),0)
		into STRICT 	qt_item_pend_oc_w
		from	ordem_compra_item_entrega
		where	nr_ordem_compra = nr_ordem_compra_p
		and 	nr_item_oci = nr_item_oci_w
		and	coalesce(dt_cancelamento::text, '') = '';
		
		qt_total_item_pend_oc_w := qt_total_item_pend_oc_w + qt_item_pend_oc_w;
		
		if (qt_total_item_pend_oc_w > 0) then
			return 1;
		end if;
			
		end;
	end loop;
	close c01;

	return	0;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
