SET client_encoding TO 'UTF8';

	
	--QT>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>Obter percentual da transacao no projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--		

	/*
	Objetivo: Retornar o percentual da tipo passado por parametro em relacao ao valor total do projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	ie_transacao_p = 'C' - Comprometido, 'R' - Realizado, 'S' - Saldo.
	*/
	


CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_perc_tipo_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

		
	/*Usado para armazenar o valor total do projeto recurso*/


	vl_total_proj_rec_w 	projeto_recurso_saldo.vl_saldo%type;
	/*Utilizardo para retornar o percentual do tipo solicitada*/


	qt_perc_trans_w  double precision;
	
	
BEGIN

	qt_perc_trans_w := 0;
	
	vl_total_proj_rec_w := projeto_recurso_pck.obter_vl_proj_rec(nr_seq_proj_rec_p);
	
	if (vl_total_proj_rec_w > 0) then
		if (upper(ie_tipo_p) = 'C') then
			
			qt_perc_trans_w :=  round(dividir((projeto_recurso_pck.obter_vl_comprometido(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p) * 100), vl_total_proj_rec_w),2);
			
		elsif (upper(ie_tipo_p) = 'R') then
			
			qt_perc_trans_w := round(dividir((projeto_recurso_pck.obter_vl_realizado(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p) * 100), vl_total_proj_rec_w),2);
			
		elsif (upper(ie_tipo_p) = 'S') then
		
			qt_perc_trans_w := round(dividir((projeto_recurso_pck.obter_vl_saldo_proj_rec(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p) * 100), vl_total_proj_rec_w),2);
		
		end if;	
	end if;
			
	return qt_perc_trans_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
