	--QT>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>Obter quantidade total vinculado ao projeto recurso (sc + oc + nf + tp) <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--	

	/*
	Objetivo: Retornar a quantidade total (sc + oc + nf + tp)  vinculado ao projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_qt_total_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
BEGIN

	return projeto_recurso_pck.obter_qt_total_sc_proj_rec(nr_seq_proj_rec_p,nm_usuario_p,cd_estabelecimento_p) +
          projeto_recurso_pck.obter_qt_total_oc_proj_rec(nr_seq_proj_rec_p,nm_usuario_p,cd_estabelecimento_p) +
          projeto_recurso_pck.obter_qt_total_nf_proj_rec(nr_seq_proj_rec_p,nm_usuario_p,cd_estabelecimento_p) +
          projeto_recurso_pck.obter_qt_total_tp_proj_rec(nr_seq_proj_rec_p,nm_usuario_p,cd_estabelecimento_p);

	END;	

$body$
