SET client_encoding TO 'UTF8';

	
	--QT>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter a quantidade de titulos a pagar do projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<--	

	/*
	Objetivo: Retornar o quantidade total de titulos a pagar do projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/
	


CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_qt_total_tp_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

				
	/* Variavel para retornar a quantidade total de titulos a pagar do projeto recurso.*/


	qt_total_tp_proj_rec		bigint;	
	
		
	
BEGIN
		
	select  coalesce(count(a.nr_titulo),0)
	into STRICT 	qt_total_tp_proj_rec
	from    titulo_pagar a
	where   a.nr_seq_proj_rec = nr_seq_proj_rec_p
	and     ie_situacao in ('A','L','T','B')-- Considera titulos a pagar com status: Aberto, Liquidado, Transferido e Bloqueado
	and	coalesce(nr_seq_nota_fiscal::text, '') = '';
	
	return qt_total_tp_proj_rec;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
