	--VS>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter saldo de material em solicitacao de compra no projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar o valor de saldo de material em solicitacoes de compra no projeto recurso passado por parametro.
	Utilizado na funcao "Controle de Projetos e Recursos Externos", aba "Projetos e recursos" > "Itens"
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	cd_material_p = Codigo de material
	nr_seq_item_p = Numero de sequencia do item na tabela projeto_recurso_item
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_saldo_proj_rec_item ( nr_seq_proj_rec_p bigint, cd_material_p bigint, nr_seq_item_p bigint default 0) RETURNS bigint AS $body$
DECLARE

			
	qt_mat_solicitado_w		solic_compra_item.qt_material%type;
	qt_mat_projeto_w		projeto_recurso_item.qt_material%type;
	qt_saldo_w			projeto_recurso_item.qt_material%type;

	
BEGIN

	select	coalesce(sum(a.qt_material),0)
	into STRICT	qt_mat_projeto_w
	from	projeto_recurso_item a
	where	a.nr_seq_proj_rec = nr_seq_proj_rec_p
	and	a.cd_material = cd_material_p
	and	((coalesce(nr_seq_item_p,0) = 0) or (coalesce(nr_seq_item_p,0) = a.nr_sequencia));

	select	coalesce(sum(b.qt_material - coalesce(b.qt_material_cancelado,0)),0)
	into STRICT	qt_mat_solicitado_w
	from 	solic_compra a,
		solic_compra_item b
	where	a.nr_solic_compra = b.nr_solic_compra
	and	b.nr_seq_proj_rec = nr_seq_proj_rec_p
	and 	b.cd_material = cd_material_p
	and 	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
	and 	coalesce(b.dt_reprovacao::text, '') = ''
	and		((coalesce(nr_seq_item_p,0) = 0) or (coalesce(nr_seq_item_p,0) = b.nr_seq_item_proj_rec));

	qt_saldo_w	:= qt_mat_projeto_w - qt_mat_solicitado_w;

	return	qt_saldo_w;

	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
