CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_inicial_proj_rec ( nr_seq_proj_rec_p bigint) RETURNS bigint AS $body$
DECLARE

			
	/* Valor inicial do projeto recurso */


	vl_inicio_projeto_w projeto_recurso.vl_inicio_projeto%type;
			
	
BEGIN
	
		select 	coalesce(max(vl_inicio_projeto),0)
		into STRICT	vl_inicio_projeto_w
		from 	projeto_recurso
		where	nr_sequencia = nr_seq_proj_rec_p;
		
	return coalesce(vl_inicio_projeto_w,0);
										
	END;

